import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

function SortableCompanyField({ field, onRemove, onLabelChange }) {
  // Drag and drop functionality
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  // Apply drag transform styles
  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  // Event handlers
  const handleRemove = () => {
    if (onRemove) {
      onRemove(field.id);
    }
  };

  const handleLabelChange = (event) => {
    if (onLabelChange) {
      onLabelChange(field.id, event.target.textContent);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      e.target.blur();
    }
  };

  return (
    <div ref={setNodeRef} style={style} className="field-item">
      {/* Drag Handle */}
      <div className="drag-handle" {...attributes} {...listeners}>
        ::
      </div>

      {/* Field Content */}
      <div className="field-content">
        <span 
          className="field-label editable-label" 
          contentEditable="true"
          suppressContentEditableWarning={true}
          onBlur={handleLabelChange}
          onKeyDown={handleKeyDown}
        >
          {field.label}
        </span>
        <span 
          id={field.id} 
          className="editable-field" 
          contentEditable="true" 
          data-placeholder={field.placeholder}
        />
      </div>

      {/* Remove Button (only for dynamic fields) */}
      {onRemove && (
        <button 
          type="button" 
          className="remove-field-btn"
          onClick={handleRemove}
          title="Remove field"
        >
          Ã—
        </button>
      )}
    </div>
  );
}

export default SortableCompanyField;
