import React, { useState, useEffect } from 'react';
import './PurchaseOrderForm.css';
import { generatePurchaseOrderXML } from './templates/PurchaseOrderTemplate';

// Import modular components
import Section1CompanyInfo from './components/Section1CompanyInfo';
import Section2PurchaseOrder from './components/Section2PurchaseOrder';
import Section3VendorShipTo from './components/Section3VendorShipTo';
import { useAIProvider } from './hooks/useAIProvider';

// Import custom hooks
import { 
  useDragAndDropSensors, 
  useCompanyFieldsDragEnd, 
  usePurchaseOrderFieldsDragEnd,
  useVendorFieldsDragEnd,
  useShipToFieldsDragEnd
} from './hooks/useDragAndDrop';

import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, horizontalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { arrayMove } from '@dnd-kit/sortable';


// Draggable Section Wrapper Component for Sections 5 & 6
function DraggableSectionWrapper({ children, id, sectionNumber }) {


  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      className={`sortable-section section-${sectionNumber}`}
      data-section={sectionNumber}
    >
      {/* Left-side drag handle */}
      <div 
        className="left-drag-handle"
        {...attributes} 
        {...listeners} 
        title="Drag to reorder sections"
      >
        ::
      </div>
      {children}
    </div>
  );
}

// Sortable column header component for line items
function SortableColumnHeader({ children, id }) {
  const { attributes, listeners, setNodeRef, transform, transition, isDragging } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    cursor: 'grab',
    userSelect: 'none'
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      {...attributes} 
      {...listeners}
      className={`sortable-column-header ${isDragging ? 'dragging' : ''}`}
    >
      {children} ::
    </div>
  );
}

// Draggable Section Header Component for Comments and Totals
function DraggableSectionHeader({ children, id }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      className="section-header"
      {...attributes} 
      {...listeners} 
      title="Drag to reorder sections"
    >
      {children}
    </div>
  );
}

// Main Purchase Order Form Component
function PurchaseOrderForm() {
  // ============================================================================
  // STATE MANAGEMENT
  // ============================================================================

  // Company fields state for Section 1
  const [companyFields, setCompanyFields] = useState([
    { id: 'company-name', label: 'Company Name:', placeholder: 'Enter company name', value: '' },
    { id: 'company-address', label: 'Street Address:', placeholder: 'Enter street address', value: '' },
    { id: 'company-city-state', label: 'City, ST ZIP:', placeholder: 'City, State ZIP', value: '' },
    { id: 'company-phone', label: 'Phone:', placeholder: '(555) 123-4567', value: '' },
    { id: 'company-fax', label: 'Fax:', placeholder: '(555) 123-4567', value: '' },
    { id: 'company-website', label: 'Website:', placeholder: 'www.example.com', value: '' }
  ]);

  // Debug: Log company fields state changes
  useEffect(() => {
    console.log('🔄 Company fields state updated:', companyFields.map(f => ({ id: f.id, label: f.label })));
  }, [companyFields]);

  // Track last modification time for company fields
  const [lastModified, setLastModified] = useState(new Date().toISOString());

  // Track last modification time for purchase order fields
  const [poLastModified, setPoLastModified] = useState(new Date().toISOString());

  // ============================================================================
  // FIELD LIST COMPONENT
  // ============================================================================

  // Hardcoded list of fields that are not currently on the form
  const availableFields = [
    // Company Information Fields
    { category: 'Company Information', fields: [
      'Tax ID Number',
      'DUNS Number',
      'Business License',
      'Industry Classification',
      'Year Established',
      'Employee Count',
      'Annual Revenue',
      'Parent Company',
      'Subsidiaries',
      'Business Type (LLC, Corp, etc.)'
    ]},
    // Purchase Order Fields
    { category: 'Purchase Order Details', fields: [
      'Delivery Date',
      'Payment Terms',
      'Currency',
      'Purchase Order Type',
      'Priority Level',
      'Department',
      'Project Code',
      'Cost Center',
      'Approval Status',
      'Approved By'
    ]},
    // Vendor Fields
    { category: 'Vendor Information', fields: [
      'Vendor Tax ID',
      'Vendor DUNS',
      'Vendor Rating',
      'Payment History',
      'Contract Terms',
      'Insurance Certificate',
      'Bond Information',
      'Quality Rating',
      'Delivery Performance',
      'Warranty Terms'
    ]},
    // Shipping Fields
    { category: 'Shipping & Delivery', fields: [
      'Expected Delivery',
      'Carrier Information',
      'Tracking Number',
      'Delivery Instructions',
      'Special Handling',
      'Packaging Requirements',
      'Delivery Confirmation',
      'Return Policy',
      'Damage Claims',
      'Freight Class'
    ]},
    // Financial Fields
    { category: 'Financial Details', fields: [
      'Discount Percentage',
      'Early Payment Discount',
      'Late Payment Penalty',
      'Installment Terms',
      'Security Deposit',
      'Performance Bond',
      'Liquidated Damages',
      'Retention Amount',
      'Change Order Process',
      'Budget Approval'
    ]},
    // Compliance Fields
    { category: 'Compliance & Legal', fields: [
      'Regulatory Compliance',
      'Safety Requirements',
      'Environmental Standards',
      'Quality Certifications',
      'Audit Requirements',
      'Documentation Standards',
      'Record Retention',
      'Confidentiality Terms',
      'Non-Compete Clauses',
      'Dispute Resolution'
    ]}
  ];

  // State for sidebar visibility on mobile
  const [sidebarVisible, setSidebarVisible] = useState(true);

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setSidebarVisible(!sidebarVisible);
  };

  // Purchase Order fields state for Section 2
  const [purchaseOrderFields, setPurchaseOrderFields] = useState([
    { id: 'po-title', label: 'Purchase Order', placeholder: '', isTitle: true, value: 'Purchase Order' },
    { id: 'po-date', label: 'DATE:', placeholder: 'MM/DD/YYYY', value: '' },
    { id: 'po-number', label: 'PO #:', placeholder: 'PO#123456', value: '' }
  ]);

  // Vendor fields state for Section 3
  const [vendorFields, setVendorFields] = useState([
    { id: 'vendor-company', label: 'Company:', placeholder: 'Vendor name', value: '' },
    { id: 'vendor-contact', label: 'Contact:', placeholder: 'Contact person', value: '' },
    { id: 'vendor-address', label: 'Address:', placeholder: 'Street address', value: '' },
    { id: 'vendor-city-state', label: 'City/State:', placeholder: 'City, ST ZIP', value: '' },
    { id: 'vendor-phone', label: 'Phone:', placeholder: '(555) 123-4567', value: '' }
  ]);

  // Ship To fields state for Section 3
  const [shipToFields, setShipToFields] = useState([
    { id: 'ship-to-name', label: 'Name:', placeholder: 'Contact name', value: '' },
    { id: 'ship-to-company', label: 'Company:', placeholder: 'Shipping company', value: '' },
    { id: 'ship-to-address', label: 'Address:', placeholder: 'Street address', value: '' },
    { id: 'ship-to-city-state', label: 'City/State:', placeholder: 'City, ST ZIP', value: '' },
    { id: 'ship-to-phone', label: 'Phone:', placeholder: '(555) 123-4567', value: '' }
  ]);

  // Totals fields state for Section 9
  const [totalsFields, setTotalsFields] = useState([
    { id: 'subtotal', label: 'SUBTOTAL:', placeholder: '$0.00', value: '$0.00', isCalculated: true },
    { id: 'tax', label: 'TAX:', placeholder: '$0.00', value: '' },
    { id: 'shipping', label: 'SHIPPING:', placeholder: '$0.00', value: '' },
    { id: 'other', label: 'OTHER:', placeholder: '$0.00', value: '' },
    { id: 'total', label: 'TOTAL:', placeholder: '$0.00', value: '$0.00', isCalculated: true }
  ]);



  // ============================================================================
  // CUSTOM HOOKS
  // ============================================================================

  const sensors = useDragAndDropSensors();
  const handleVendorDragEnd = useVendorFieldsDragEnd(setVendorFields);
  const handleShipToDragEnd = useShipToFieldsDragEnd(setShipToFields);

  // ============================================================================
  // COMPANY FIELD MANAGEMENT FUNCTIONS
  // ============================================================================

  // Helper function to update timestamp when company fields change
  const updateCompanyFieldsTimestamp = () => {
    setLastModified(new Date().toISOString());
  };

  // Add new company field
  const handleAddCompanyField = (newField) => {
    setCompanyFields(prevFields => [...prevFields, newField]);
    updateCompanyFieldsTimestamp();

    logChange('field-add', newField.id, '', newField.label, 'company');

    console.log('➕ Added new company field:', newField);
  };

  // Remove company field
  const handleRemoveCompanyField = (fieldId) => {
    setCompanyFields(prevFields => {
      const fieldToRemove = prevFields.find(field => field.id === fieldId);

      showNotification(`🗑️ Removed company field: "${fieldToRemove?.label}"`, 'warning');

      console.log('🗑️ Removed company field:', fieldToRemove);
      return prevFields.filter(field => field.id !== fieldId);
    });
    updateCompanyFieldsTimestamp();
  };

  // ============================================================================
  // ENHANCED CHANGE TRACKING
  // ============================================================================

  // State for tracking all changes
  const [changeHistory, setChangeHistory] = useState([]);
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const [lastExportTime, setLastExportTime] = useState(null);




  // Function to log changes with timestamps
  const logChange = (changeType, fieldId, oldValue, newValue, section) => {
    const change = {
      id: Date.now(),
      timestamp: new Date().toISOString(),
      type: changeType,
      fieldId,
      oldValue,
      newValue,
      section
    };

    setChangeHistory(prev => [change, ...prev.slice(0, 49)]); // Keep last 50 changes
    console.log('📝 Change logged:', change);
  };

  // Enhanced field update functions with change logging
  const handleCompanyFieldLabelChange = (fieldId, newLabel) => {
    setCompanyFields(prevFields => {
      const oldField = prevFields.find(field => field.id === fieldId);
      const oldLabel = oldField ? oldField.label : '';

      logChange('label-change', fieldId, oldLabel, newLabel, 'company');

      return prevFields.map(field => 
        field.id === fieldId ? { ...field, label: newLabel } : field
      );
    });
    updateCompanyFieldsTimestamp();
  };

  const handlePurchaseOrderFieldLabelChange = (fieldId, newLabel) => {
    setPurchaseOrderFields(prevFields => {
      const oldField = prevFields.find(field => field.id === fieldId);
      const oldLabel = oldField ? oldField.label : '';

      logChange('label-change', fieldId, oldLabel, newLabel, 'purchase-order');

      return prevFields.map(field => 
        field.id === fieldId ? { ...field, label: newLabel } : field
      );
    });
    updatePurchaseOrderFieldsTimestamp();
  };

  // Function to handle content changes in editable fields
  const handleContentChange = (fieldId, newContent, section) => {
    // Find the field and log the change
    let oldValue = '';
    let fieldType = '';

    if (section === 'company') {
      const field = companyFields.find(f => f.id === fieldId);
      if (field) {
        oldValue = field.value || '';
        fieldType = field.label;
      }
    } else if (section === 'purchase-order') {
      const field = purchaseOrderFields.find(f => f.id === fieldId);
      if (field) {
        oldValue = field.value || '';
        fieldType = field.label;
      }
    }

    if (oldValue !== newContent) {
      logChange('content-change', fieldId, oldValue, newContent, section);
    }
  };



  // Function to toggle preview mode
  const togglePreviewMode = () => {
    setIsPreviewMode(!isPreviewMode);
  };

  // Function to clear change history
  const clearChangeHistory = () => {
    setChangeHistory([]);
    console.log('🗑️ Change history cleared');
  };

  // ============================================================================
  // XML DISPLAY MODAL
  // ============================================================================

  // State for XML modal
  const [showXMLModal, setShowXMLModal] = useState(false);
  const [xmlOutput, setXmlOutput] = useState('');
  // UI toggle: show field pills (placeholders) vs live values
  const [showFieldPills, setShowFieldPills] = useState(false);

  // When showing field pills, set data-placeholder attributes to NS-style tokens
  useEffect(() => {
    if (showFieldPills) {
      // Store original values and show field pills
      const setPH = (selector, token) => {
        const el = document.querySelector(selector);
        if (el) {
          // Store original content if not already stored
          if (!el.hasAttribute('data-original-content')) {
            const currentContent = el.textContent || '';
            // Only store if it's not already a field pill token
            if (!currentContent.startsWith('$')) {
              el.setAttribute('data-original-content', currentContent);
            }
          }
          el.setAttribute('data-placeholder', token);
          // Show the field pill token
          el.textContent = token;
        }
      };

    // Company
    setPH('#company-name, [data-field="company-name"] .editable-field', '$company_name');
    setPH('#company-address, [data-field="company-address"] .editable-field', '$company_address');
    setPH('#company-city-state, [data-field="company-city-state"] .editable-field', '$company_city_state');
    setPH('#company-phone, [data-field="company-phone"] .editable-field', '$phone');
    setPH('#company-fax, [data-field="company-fax"] .editable-field', '$fax');
    setPH('#company-website, [data-field="company-website"] .editable-field', '$website');

    // PO header
    setPH('#po-date, [data-field="po-date"] .editable-field, [data-field="po-date"]', '$po_date');
    setPH('#po-number, [data-field="po-number"] .editable-field, [data-field="po-number"]', '$po_number');

    // Vendor
    setPH('#vendor-company, [data-field="vendor-company"] .editable-field', '$vendor_company');
    setPH('#vendor-contact, [data-field="vendor-contact"] .editable-field', '$vendor_contact');
    setPH('#vendor-address, [data-field="vendor-address"] .editable-field', '$vendor_address');
    setPH('#vendor-city-state, [data-field="vendor-city-state"] .editable-field', '$vendor_city_state');
    setPH('#vendor-phone, [data-field="vendor-phone"] .editable-field', '$vendor_phone');

    // Ship-To
    setPH('#ship-to-name, [data-field="ship-to-name"] .editable-field', '$ship_to_name');
    setPH('#ship-to-company, [data-field="ship-to-company"] .editable-field', '$ship_to_company');
    setPH('#ship-to-address, [data-field="ship-to-address"] .editable-field', '$ship_to_address');
    setPH('#ship-to-city-state, [data-field="ship-to-city-state"] .editable-field', '$ship_to_city_state');
    setPH('#ship-to-phone, [data-field="ship-to-phone"] .editable-field', '$ship_to_phone');

    // Shipping details
    setPH('.shipping-details [data-field="requisitioner"] .editable-field', '$requisitioner');
    setPH('.shipping-details [data-field="shipVia"] .editable-field', '$ship_via');
    setPH('.shipping-details [data-field="fob"] .editable-field', '$fob');
    setPH('.shipping-details [data-field="shippingTerms"] .editable-field', '$shipping_terms');

    // Line items (first 5 rows)
    for (let i = 1; i <= 5; i++) {
      setPH(`.itemtable tbody tr:nth-child(${i}) td[data-column="itemNumber"] .editable-field`, `$item_number${i}`);
      setPH(`.itemtable tbody tr:nth-child(${i}) td[data-column="description"] .editable-field`, `$description${i}`);
      setPH(`.itemtable tbody tr:nth-child(${i}) td[data-column="qty"] .editable-field`, `$qty${i}`);
      setPH(`.itemtable tbody tr:nth-child(${i}) td[data-column="rate"] .editable-field`, `$rate${i}`);
      setPH(`.itemtable tbody tr:nth-child(${i}) td[data-column="amount"] .editable-field`, `$amount${i}`);
    }

    // Totals (editable ones)
    setPH('.totals-section .total-row[data-field="tax"] .editable-field', '$tax');
    setPH('.totals-section .total-row[data-field="shipping"] .editable-field', '$shipping');
    setPH('.totals-section .total-row[data-field="other"] .editable-field', '$other');

    // Comments & contact
    setPH('.comments-content .editable-field', '$comments');
    setPH('.contact-section .editable-field', '$contact_info');

    // Log summary of field pills processing
    console.log(`🎯 Field pills processing complete. Check console for any missing fields.`);
    } else {
      // Restore original values when field pills are turned off
      const restoreOriginal = (selector) => {
        const el = document.querySelector(selector);
        if (el && el.hasAttribute('data-original-content')) {
          const originalContent = el.getAttribute('data-original-content');
          el.textContent = originalContent;
          el.removeAttribute('data-original-content');
          console.log(`🔄 Restored original content for ${selector}: ${originalContent}`);
        } else if (el) {
          // If no original content stored, try to restore from placeholder or default
          const placeholder = el.getAttribute('data-placeholder');
          if (placeholder) {
            el.textContent = placeholder;
            console.log(`🔄 Restored placeholder for ${selector}: ${placeholder}`);
          }
        }
      };

      // Restore all fields
      const allSelectors = [
        // Company fields
        '#company-name, [data-field="company-name"] .editable-field',
        '#company-address, [data-field="company-address"] .editable-field',
        '#company-city-state, [data-field="company-city-state"] .editable-field',
        '#company-phone, [data-field="company-phone"] .editable-field',
        '#company-fax, [data-field="company-fax"] .editable-field',
        '#company-website, [data-field="company-website"] .editable-field',

        // PO fields
        '#po-date, [data-field="po-date"] .editable-field',
        '#po-number, [data-field="po-number"] .editable-field',

        // Vendor fields
        '[data-field="vendor-company"] .editable-field',
        '[data-field="vendor-contact"] .editable-field',
        '[data-field="vendor-address"] .editable-field',
        '[data-field="vendor-city-state"] .editable-field',
        '[data-field="vendor-phone"] .editable-field',

        // Ship-to fields
        '[data-field="ship-to-name"] .editable-field',
        '[data-field="ship-to-company"] .editable-field',
        '[data-field="ship-to-address"] .editable-field',
        '[data-field="ship-to-city-state"] .editable-field',
        '[data-field="ship-to-phone"] .editable-field',

        // Shipping details
        '.shipping-details [data-field="requisitioner"] .editable-field',
        '.shipping-details [data-field="shipVia"] .editable-field',
        '.shipping-details [data-field="fob"] .editable-field',
        '.shipping-details [data-field="shippingTerms"] .editable-field',

        // Line items (first 5 rows)
        '.itemtable tbody tr:nth-child(1) td[data-column="itemNumber"] .editable-field',
        '.itemtable tbody tr:nth-child(1) td[data-column="description"] .editable-field',
        '.itemtable tbody tr:nth-child(1) td[data-column="qty"] .editable-field',
        '.itemtable tbody tr:nth-child(1) td[data-column="rate"] .editable-field',
        '.itemtable tbody tr:nth-child(1) td[data-column="amount"] .editable-field',
        '.itemtable tbody tr:nth-child(2) td[data-column="itemNumber"] .editable-field',
        '.itemtable tbody tr:nth-child(2) td[data-column="description"] .editable-field',
        '.itemtable tbody tr:nth-child(2) td[data-column="qty"] .editable-field',
        '.itemtable tbody tr:nth-child(2) td[data-column="rate"] .editable-field',
        '.itemtable tbody tr:nth-child(2) td[data-column="amount"] .editable-field',
        '.itemtable tbody tr:nth-child(3) td[data-column="itemNumber"] .editable-field',
        '.itemtable tbody tr:nth-child(3) td[data-column="description"] .editable-field',
        '.itemtable tbody tr:nth-child(3) td[data-column="qty"] .editable-field',
        '.itemtable tbody tr:nth-child(3) td[data-column="rate"] .editable-field',
        '.itemtable tbody tr:nth-child(3) td[data-column="amount"] .editable-field',
        '.itemtable tbody tr:nth-child(4) td[data-column="itemNumber"] .editable-field',
        '.itemtable tbody tr:nth-child(4) td[data-column="description"] .editable-field',
        '.itemtable tbody tr:nth-child(4) td[data-column="qty"] .editable-field',
        '.itemtable tbody tr:nth-child(4) td[data-column="rate"] .editable-field',
        '.itemtable tbody tr:nth-child(4) td[data-column="amount"] .editable-field',
        '.itemtable tbody tr:nth-child(5) td[data-column="itemNumber"] .editable-field',
        '.itemtable tbody tr:nth-child(5) td[data-column="description"] .editable-field',
        '.itemtable tbody tr:nth-child(5) td[data-column="qty"] .editable-field',
        '.itemtable tbody tr:nth-child(5) td[data-column="rate"] .editable-field',
        '.itemtable tbody tr:nth-child(5) td[data-column="amount"] .editable-field',

        // Totals
        '.totals-section .total-row[data-field="tax"] .editable-field',
        '.totals-section .total-row[data-field="shipping"] .editable-field',
        '.totals-section .total-row[data-field="other"] .editable-field',

        // Comments & contact
        '.comments-content .editable-field',
        '.contact-section .editable-field'
      ];

      allSelectors.forEach(restoreOriginal);

      // Additional cleanup: remove any remaining field pill attributes
      const cleanupFieldPills = () => {
        const allEditableFields = document.querySelectorAll('.editable-field');
        allEditableFields.forEach(field => {
          if (field.hasAttribute('data-placeholder') && field.textContent.startsWith('$')) {
            // This field still has a field pill token, restore it
            const placeholder = field.getAttribute('data-placeholder');
            if (placeholder && !placeholder.startsWith('$')) {
              field.textContent = placeholder;
            }
            field.removeAttribute('data-placeholder');
          }
        });
      };

      cleanupFieldPills();
      console.log(`🔄 Field pills turned off - original values restored and cleanup completed`);
    }
  }, [showFieldPills]);

  // Function to display XML output in a modal
  const displayXMLOutput = (xml) => {
    setXmlOutput(xml);
    setShowXMLModal(true);
  };

  // Function to close XML modal
  const closeXMLModal = () => {
    setShowXMLModal(false);
    setXmlOutput('');
  };

  // Function to copy XML to clipboard
  const copyXMLToClipboard = () => {
    navigator.clipboard.writeText(xmlOutput).then(() => {
      showNotification('📋 XML copied to clipboard!', 'success');
    }).catch(() => {
      showNotification('❌ Failed to copy XML to clipboard', 'error');
    });
  };

  // Function to download XML file
  const downloadXML = () => {
    const blob = new Blob([xmlOutput], { type: 'application/xml' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `purchase-order-${purchaseOrderFields.find(f => f.id === 'po-number')?.value || 'unknown'}.xml`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
    showNotification('📥 XML downloaded successfully!', 'success');
  };

  // =========================================================================
  // AI SUGGESTIONS
  // =========================================================================
  const { getFieldSuggestions } = useAIProvider();

  const applySuggestionsToFields = (suggestionsMap, fields) => {
    if (!Array.isArray(fields)) return fields;
    return fields.map(f => {
      const newVal = suggestionsMap?.[f.id];
      if (typeof newVal === 'string') {
        return { ...f, value: newVal };
      }
      return f;
    });
  };

  const handleAIFill = async () => {
    try {
      showNotification('🤖 Fetching AI suggestions...', 'info');

      const payload = {
        context: 'purchase_order',
        companyFields,
        purchaseOrderFields,
        vendorFields,
        shipToFields,
        shippingFields: [
          { id: 'requisitioner', label: 'Requisitioner name' },
          { id: 'shipVia', label: 'Shipping method' },
          { id: 'fob', label: 'FOB terms' },
          { id: 'shippingTerms', label: 'Shipping terms' }
        ]
      };

      const { suggestions } = await getFieldSuggestions(payload);
      try {
        console.debug('🧪 AI suggestions keys:', Object.keys(suggestions || {}));
        // Preview a few critical values to verify mapping
