import React from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import SortableCompanyField from './SortableCompanyField';

// Draggable Section Header Component
function DraggableSectionHeader({ children, id }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      className="section-header"
      {...attributes} 
      {...listeners} 
      title="Drag to reorder sections"
    >
      {children}
      <span className="drag-indicator" style={{ 
        float: 'right', 
        opacity: 1,
        fontSize: '12px',
        marginRight: '8px',
        color: '#7c3aed'
      }}>
        ::
      </span>
    </div>
  );
}

function Section1CompanyInfo({ 
  companyFields, 
  sensors, 
  onCompanyDragEnd,
  onAddCompanyField,
  onRemoveCompanyField,
  onLabelChange,
  onContentChange,
  lastModified
}) {
  // Handler for adding new company fields
  const handleAddField = () => {
    const newField = {
      id: `company-field-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      label: 'New Field:',
      placeholder: 'Enter field value'
    };
    onAddCompanyField(newField);
  };

  // Log field changes to console for tracking
  React.useEffect(() => {
    // Only log when there are actual changes, not on initial render
    if (companyFields.length > 0) {
      console.log('ðŸ”„ Company Fields Updated:', {
        totalFields: companyFields.length,
        lastModified: new Date(lastModified).toLocaleString(),
        fields: companyFields.map((field, index) => ({
          position: index + 1,
          id: field.id,
          label: field.label,
          placeholder: field.placeholder
        }))
      });
    }
  }, [companyFields, lastModified]);

  return (
    <div className="company-info-section">
      {/* Section Header with Drag Handle */}
      <DraggableSectionHeader id="section1">
        Section 1
      </DraggableSectionHeader>

      {/* Company Fields with Drag and Drop */}
      <DndContext 
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={onCompanyDragEnd}
      >
        <SortableContext 
          items={companyFields.map(field => field.id)}
          strategy={verticalListSortingStrategy}
        >
          <div className="company-fields-container">
            {companyFields.map(field => (
              <SortableCompanyField 
                key={field.id} 
                field={field} 
                onRemove={onRemoveCompanyField}
                onLabelChange={onLabelChange}
                onContentChange={onContentChange}
                section="company"
              />
            ))}
          </div>
        </SortableContext>
      </DndContext>

      {/* Add Field Button */}
      <div className="add-field-section">
        <button 
          type="button" 
          className="add-field-btn"
          onClick={handleAddField}
          title="Add new company field"
        >
          +
        </button>
      </div>


    </div>
  );
}

export default Section1CompanyInfo;
