import React from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import SortablePurchaseOrderField from './SortablePurchaseOrderField';

// Draggable Section Header Component
function DraggableSectionHeader({ children, id }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      className="section-header"
      {...attributes} 
      {...listeners} 
      title="Drag to reorder sections"
    >
      {children}
      <span className="drag-indicator" style={{ 
        float: 'right', 
        opacity: 0.7, 
        fontSize: '12px',
        marginRight: '8px'
      }}>
        ::
      </span>
    </div>
  );
}

function Section2PurchaseOrder({ 
  purchaseOrderFields, 
  sensors, 
  onPurchaseOrderDragEnd,
  onAddPurchaseOrderField,
  onRemovePurchaseOrderField,
  onLabelChange,
  onContentChange,
  lastModified
}) {
  // Handler for adding new purchase order fields
  const handleAddField = () => {
    const newField = {
      id: `po-field-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      label: 'New Field:',
      placeholder: 'Enter field value'
    };
    onAddPurchaseOrderField(newField);
  };

  // Log field changes to console for tracking
  React.useEffect(() => {
    // Only log when there are actual changes, not on initial render
    if (purchaseOrderFields.length > 0) {
      console.log('ðŸ”„ Purchase Order Fields Updated:', {
        totalFields: purchaseOrderFields.length,
        lastModified: new Date(lastModified).toLocaleString(),
        fields: purchaseOrderFields.map((field, index) => ({
          position: index + 1,
          id: field.id,
          label: field.label,
          placeholder: field.placeholder,
          isTitle: field.isTitle || false
        }))
      });
    }
  }, [purchaseOrderFields, lastModified]);

  return (
    <div className="section-2-purchase-order">
      {/* Section Header with Drag Handle */}
      <DraggableSectionHeader id="section2">
        Section 2
      </DraggableSectionHeader>

      {/* Purchase Order Fields with Drag and Drop */}
      <DndContext 
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={onPurchaseOrderDragEnd}
      >
        <SortableContext 
          items={purchaseOrderFields.map(field => field.id)}
          strategy={verticalListSortingStrategy}
        >
          <div className="po-fields-container">
            {purchaseOrderFields.map(field => (
              <SortablePurchaseOrderField 
                key={field.id} 
                field={field} 
                onRemove={onRemovePurchaseOrderField}
                onLabelChange={onLabelChange}
                onContentChange={onContentChange}
                section="purchase-order"
              />
            ))}
          </div>
        </SortableContext>
      </DndContext>

      {/* Add Field Button */}
      <div className="add-field-section">
        <button 
          type="button" 
          className="add-field-btn"
          onClick={handleAddField}
          title="Add new purchase order field"
        >
          +
        </button>
      </div>


    </div>
  );
}

export default Section2PurchaseOrder;
