import React from 'react';
import { useDraggable } from '@dnd-kit/core';
import './AvailableFields.css';

// Available fields data
const availableFieldsData = [
  // Company Information Fields
  { category: 'Company Information', fields: [
    'Tax ID Number',
    'DUNS Number',
    'Business License',
    'Industry Classification',
    'Year Established',
    'Employee Count',
    'Annual Revenue',
    'Parent Company',
    'Subsidiaries',
    'Business Type (LLC, Corp, etc.)'
  ]},
  // Purchase Order Fields
  { category: 'Purchase Order Details', fields: [
    'Delivery Date',
    'Payment Terms',
    'Currency',
    'Purchase Order Type',
    'Priority Level',
    'Department',
    'Project Code',
    'Cost Center',
    'Approval Status',
    'Approved By'
  ]},
  // Vendor Fields
  { category: 'Vendor Information', fields: [
    'Vendor Tax ID',
    'Vendor DUNS',
    'Vendor Rating',
    'Payment History',
    'Contract Terms',
    'Insurance Certificate',
    'Bond Information',
    'Quality Rating',
    'Delivery Performance',
    'Warranty Terms'
  ]},
  // Shipping Fields
  { category: 'Shipping & Delivery', fields: [
    'Expected Delivery',
    'Carrier Information',
    'Tracking Number',
    'Delivery Instructions',
    'Special Handling',
    'Packaging Requirements',
    'Delivery Confirmation',
    'Return Policy',
    'Damage Claims',
    'Freight Class'
  ]},
  // Financial Fields
  { category: 'Financial Details', fields: [
    'Discount Percentage',
    'Early Payment Discount',
    'Late Payment Penalty',
    'Installment Terms',
    'Security Deposit',
    'Performance Bond',
    'Liquidated Damages',
    'Retention Amount',
    'Change Order Process',
    'Budget Approval'
  ]},
  // Compliance Fields
  { category: 'Compliance & Legal', fields: [
    'Regulatory Compliance',
    'Safety Requirements',
    'Environmental Standards',
    'Quality Certifications',
    'Audit Requirements',
    'Documentation Standards',
    'Record Retention',
    'Confidentiality Terms',
    'Non-Compete Clauses',
    'Dispute Resolution'
  ]}
];

// Draggable field item component
const PaletteDraggable = ({ label, category, onAddField, showNotification }) => {
  const id = `palette-${category}-${label}`;
  const { attributes, listeners, setNodeRef, transform, isDragging } = useDraggable({
    id,
    data: { 
      source: 'palette', 
      label, 
      category,
      type: 'palette-field'
    }
  });

  const style = {
    transform: transform ? `translate3d(${transform.x}px, ${transform.y}px, 0)` : undefined,
    cursor: 'grab',
    opacity: isDragging ? 0.6 : 1
  };

  const handleAddClick = (e) => {
    e.stopPropagation();
    if (onAddField) {
      onAddField(label, category);
    }
    if (showNotification) {
      showNotification(`âž• ${label} field added to form`, 'info');
    }
  };

  return (
    <li ref={setNodeRef} style={style} className="field-item" {...attributes} {...listeners}>
      <span className="field-name">{label}</span>
      <button 
        className="add-field-btn" 
        title={`Add ${label} to form`} 
        onClick={handleAddClick}
      >
        +
      </button>
    </li>
  );
};

// Main AvailableFields component
const AvailableFields = ({ 
  isVisible = true, 
  onToggleVisibility, 
  onAddField, 
  showNotification 
}) => {
  return (
    <>
      {/* Sidebar Toggle Button */}
      <div className="sidebar-toggle-container">
        <button 
          className="sidebar-toggle-btn"
          onClick={onToggleVisibility}
          title={isVisible ? "Hide Field List" : "Show Field List"}
        >
          {isVisible ? "ðŸ“‹ Hide Fields" : "ðŸ“‹ Show Fields"}
        </button>
      </div>

      {/* Field List Sidebar */}
      {isVisible && (
        <div className="field-list-sidebar">
          <div className="field-list-header">
            <h3>ðŸ“‹ Available Fields</h3>
            <p>Fields not currently on the form</p>
          </div>
          <div className="field-list-content">
            {availableFieldsData.map((category, categoryIndex) => (
              <div key={categoryIndex} className="field-category">
                <h4 className="category-title">{category.category}</h4>
                <ul className="field-list">
                  {category.fields.map((field, fieldIndex) => (
                    <PaletteDraggable 
                      key={`${categoryIndex}-${fieldIndex}`} 
                      label={field} 
                      category={category.category}
                      onAddField={onAddField}
                      showNotification={showNotification}
                    />
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
};

export default AvailableFields;
export { availableFieldsData };
