import React, { useState } from 'react';
import './PurchaseOrderForm.css';

// Import modular components
import Section1CompanyInfo from './components/Section1CompanyInfo';
import Section2PurchaseOrder from './components/Section2PurchaseOrder';

// Import custom hooks
import { 
  useDragAndDropSensors, 
  useCompanyFieldsDragEnd, 
  usePurchaseOrderFieldsDragEnd 
} from './hooks/useDragAndDrop';

// Import field configurations
import { companyFieldsConfig, purchaseOrderFieldsConfig } from './data/fieldConfigs';

// Sortable Company Field Component
function SortableCompanyField({ field }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} className="field-item">
      <div className="drag-handle" {...attributes} {...listeners}>
        ::
      </div>
      <div className="field-content">
        <span className="field-label">{field.label}</span>
        <span 
          id={field.id} 
          className="editable-field" 
          contentEditable="true" 
          data-placeholder={field.placeholder}
        />
      </div>
    </div>
  );
}

// Sortable Purchase Order Field Component
function SortablePurchaseOrderField({ field }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} className="po-field-item">
      <div className="drag-handle" {...attributes} {...listeners}>
        ::
      </div>
      <div className="po-field-content">
        {field.isTitle ? (
          <div className="po-title">{field.label}</div>
        ) : (
          <>
            <span className="po-label">{field.label}</span>
            <span 
              id={field.id} 
              className="editable-field" 
              contentEditable="true" 
              data-placeholder={field.placeholder}
            />
          </>
        )}
      </div>
    </div>
  );
}

// Main Purchase Order Form Component
function PurchaseOrderForm() {
  // Company fields state
  const [companyFields, setCompanyFields] = useState([
    { id: 'company-name', label: 'Company Name:', placeholder: 'Enter company name' },
    { id: 'company-address', label: 'Street Address:', placeholder: 'Enter street address' },
    { id: 'company-city-state', label: 'City, ST ZIP:', placeholder: 'City, State ZIP' },
    { id: 'company-phone', label: 'Phone:', placeholder: '(555) 123-4567' },
    { id: 'company-fax', label: 'Fax:', placeholder: '(555) 123-4567' },
    { id: 'company-website', label: 'Website:', placeholder: 'www.example.com' }
  ]);

  // Purchase Order fields state for Section 2
  const [purchaseOrderFields, setPurchaseOrderFields] = useState([
    { id: 'po-title', label: 'Purchase Order', placeholder: '', isTitle: true },
    { id: 'po-date', label: 'DATE:', placeholder: 'MM/DD/YYYY' },
    { id: 'po-number', label: 'PO #:', placeholder: 'PO#123456' }
  ]);

  // Sensors setup
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Handle drag end for company fields
  function handleCompanyDragEnd(event) {
    const { active, over } = event;

    if (active.id !== over.id) {
      setCompanyFields((items) => {
        const oldIndex = items.findIndex(field => field.id === active.id);
        const newIndex = items.findIndex(field => field.id === over.id);

        return arrayMove(items, oldIndex, newIndex);
      });
    }
  }

  // Handle drag end for purchase order fields
  function handlePurchaseOrderDragEnd(event) {
    const { active, over } = event;

    if (active.id !== over.id) {
      setPurchaseOrderFields((items) => {
        const oldIndex = items.findIndex(field => field.id === active.id);
        const newIndex = items.findIndex(field => field.id === over.id);

        return arrayMove(items, oldIndex, newIndex);
      });
    }
  }

  return (
    <div className="purchase-order-container">
      {/* Page Header */}
      <header className="page-header">
        <h1>Purchase Order Management System</h1>
        <p>Create, edit, and manage purchase orders with ease</p>
      </header>

      {/* Purchase Order Form */}
      <div className="container">
        <div className="form-content">
          {/* Header Section */}
          <div className="section-container">
            <div className="header-section">
              <div className="company-info-section">
                <div className="section-title">Section 1</div>

                {/* Company Fields with Drag and Drop */}
                <DndContext 
                  sensors={sensors}
                  collisionDetection={closestCenter}
                  onDragEnd={handleDragEnd}
                >
                  <SortableContext 
                    items={companyFields.map(field => field.id)}
                    strategy={verticalListSortingStrategy}
                  >
                    <div className="company-fields-container">
                      {companyFields.map(field => (
                        <SortableCompanyField key={field.id} field={field} />
                      ))}
                    </div>
                  </SortableContext>
                </DndContext>
              </div>

              <div className="purchase-order-section">
                <div className="po-title">Purchase Order</div>
                <div className="po-details">
                  <div className="po-field">
                    <span className="po-label">DATE:</span>
                    <span className="editable-field" contentEditable="true" data-placeholder="MM/DD/YYYY" />
                  </div>
                  <div className="po-field">
                    <span className="po-label">PO #:</span>
                    <span className="editable-field" contentEditable="true" data-placeholder="PO#123456" />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Vendor and Ship To Section */}
          <div className="vendor-shipping-section">
            <div className="vendor-section">
              <div className="section-header">VENDOR</div>
              <div className="section-content">
                <div className="field-row">Company: <span className="editable-field" contentEditable="true" data-placeholder="Vendor name" /></div>
                <div className="field-row">Contact: <span className="editable-field" contentEditable="true" data-placeholder="Contact person" /></div>
                <div className="field-row">Address: <span className="editable-field" contentEditable="true" data-placeholder="Street address" /></div>
                <div className="field-row">City/State: <span className="editable-field" contentEditable="true" data-placeholder="City, ST ZIP" /></div>
                <div className="field-row">Phone: <span className="editable-field" contentEditable="true" data-placeholder="(555) 123-4567" /></div>
              </div>
            </div>

            <div className="ship-to-section">
              <div className="section-header">SHIP TO</div>
              <div className="section-content">
                <div className="field-row">Name: <span className="editable-field" contentEditable="true" data-placeholder="Contact name" /></div>
                <div className="field-row">Company: <span className="editable-field" contentEditable="true" data-placeholder="Shipping company" /></div>
                <div className="field-row">Address: <span className="editable-field" contentEditable="true" data-placeholder="Street address" /></div>
                <div className="field-row">City/State: <span className="editable-field" contentEditable="true" data-placeholder="City, ST ZIP" /></div>
                <div className="field-row">Phone: <span className="editable-field" contentEditable="true" data-placeholder="(555) 123-4567" /></div>
              </div>
            </div>
          </div>

          {/* Shipping Details */}
          <div className="shipping-details">
            <div className="section-header">REQUISITIONER</div>
            <div className="section-header">SHIP VIA</div>
            <div className="section-header">F.O.B.</div>
            <div className="section-header">SHIPPING TERMS</div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Requisitioner name" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping method" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="FOB terms" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping terms" /></div>
          </div>

          {/* Items Table */}
          <table className="itemtable">
            <thead>
              <tr>
                <th></th>
                <th colSpan="3">Item#</th>
                <th colSpan="12">Description</th>
                <th colSpan="2">Qty</th>
                <th colSpan="3">Rate</th>
                <th colSpan="3">Amount</th>
              </tr>
            </thead>
            <tbody>
              {[1, 2, 3, 4, 5].map((row) => (
                <tr key={row} className="draggable-row">
                  <td></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="Item number" /></td>
                  <td colSpan="12"><span className="editable-field" contentEditable="true" data-placeholder="Item description" /></td>
                  <td colSpan="2"><span className="editable-field" contentEditable="true" data-placeholder="Qty" /></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="$0.00" /></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="$0.00" /></td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Comments and Totals */}
          <div className="comments-totals-section">
            <div className="comments-section">
              <div className="section-header">Comments or Special Instructions</div>
              <div className="comments-content">
                <span className="editable-field" contentEditable="true" data-placeholder="Enter comments or special instructions..." />
              </div>
            </div>

            <div className="totals-section">
              <div className="total-row">
                <span>SUBTOTAL:</span>
                <span className="calculated-field">$0.00</span>
              </div>
              <div className="total-row">
                <span>TAX:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row">
                <span>SHIPPING:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row">
                <span>OTHER:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row total-final">
                <span>TOTAL:</span>
                <span className="calculated-field total-field">$0.00</span>
              </div>
            </div>
          </div>

          {/* Contact Information */}
          <div className="contact-section">
            <strong>Contact Information:</strong><br />
            <span className="editable-field" contentEditable="true" data-placeholder="Enter contact information here" />
          </div>

          {/* Convert Button */}
          <div className="button-section">
            <button className="btn">Convert to NetSuite XML</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PurchaseOrderForm;
