    fields: ['companyName', 'companyAddress', 'companyPhone', 'companyEmail']
  },
  purchaseOrder: {
    fields: ['poNumber', 'poDate', 'dueDate', 'status']
  },
  vendor: {
    fields: ['vendorName', 'vendorAddress', 'vendorPhone', 'vendorEmail', 'vendorTaxId']
  },
  shipTo: {
    fields: ['shipToName', 'shipToAddress', 'shipToCity', 'shipToState', 'shipToZip']
  },
  shipping: {
    fields: ['requisitioner', 'shipVia', 'fob', 'shippingTerms']
