import React, { useState, useEffect } from 'react';
import { useDroppable } from '@dnd-kit/core';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

// Draggable Section Wrapper Component
function DraggableSectionWrapper({ children, id, sectionNumber, isSectionHandleDragging = false, showDragHandle = true }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging
  } = useSortable({ 
    id, 
    data: {
      type: 'section',
      sectionNumber: sectionNumber
    }
  });

  // Map section numbers to proper CSS classes
  const getSectionClass = (sectionNum) => {
    switch(sectionNum) {
      case 1:
        return 'section-1-company-info';
      case 2:
        return 'section-2-purchase-order';
      default:
        return `section-${sectionNum}`;
    }
  };

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.6 : 1,
    zIndex: isDragging ? 1000 : 1,
    cursor: isDragging ? 'grabbing' : 'grab',
    position: 'relative',
    width: '100%',
    height: '100%',
    border: isDragging ? '3px solid #ff6b6b' : '2px solid #ddd',
    borderRadius: '8px',
    backgroundColor: isDragging ? 'rgba(255, 107, 107, 0.1)' : 'transparent'
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className={`sortable-section ${getSectionClass(sectionNumber)}`}
      {...attributes}
    >
      {showDragHandle && (
        <div
          className="section-drag-handle"
          {...listeners}
          style={{
            position: 'absolute',
            left: '-35px',
            top: '50%',
            transform: 'translateY(-50%)',
            cursor: 'grab',
            color: '#3b82f6',
            fontSize: '14px',
            zIndex: '10',
            opacity: isSectionHandleDragging ? '0.7' : '1',
            background: 'white',
            padding: '6px 8px',
            borderRadius: '8px',
            border: '2px solid #3b82f6',
            boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
            transition: 'all 0.2s',
            userSelect: 'none',
            fontFamily: 'monospace',
            fontWeight: 'bold'
          }}
          title="Drag to move shipping section"
        >
          ⋮⋮
        </div>
      )}
      {children}
    </div>
  );
}

// Sortable Shipping Column Header Component
function SortableShippingColumnHeader({ children, id, onDragEnd }) {
  const { attributes, listeners, setNodeRef, transform, transition, isDragging } = useSortable({ 
    id,
    data: {
      type: 'shipping-column',
      columnId: id
    }
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    cursor: 'grab',
    userSelect: 'none',
    opacity: isDragging ? 0.8 : 1,
    zIndex: isDragging ? 1000 : 1
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      {...attributes} 
      {...listeners}
      className={`sortable-shipping-column-header ${isDragging ? 'dragging' : ''}`}
      title="Drag to reorder shipping columns"
    >
      {children}
    </div>
  );
}

const Section5Shipping = ({ shippingColumnOrder = ['requisitioner', 'shipVia', 'fob', 'shippingTerms'], onShippingColumnDragEnd }) => {
  const [isPaletteOver, setIsPaletteOver] = useState(false);

  const { setNodeRef, isOver } = useDroppable({
    id: 'section5',
  });

  // Update isPaletteOver when isOver changes
  useEffect(() => {
    setIsPaletteOver(isOver);
  }, [isOver]);

  // Column configuration mapping
  const columnConfig = {
    requisitioner: { label: 'REQUISITIONER', placeholder: 'Requisitioner name' },
    shipVia: { label: 'SHIP VIA', placeholder: 'Shipping method' },
    fob: { label: 'F.O.B.', placeholder: 'FOB terms' },
    shippingTerms: { label: 'SHIPPING TERMS', placeholder: 'Shipping terms' }
  };

  // Handle column reordering
  const handleColumnDragEnd = (event) => {
    const { active, over } = event;

    if (active.id !== over.id) {
      const oldIndex = shippingColumnOrder.indexOf(active.id.replace('shipping-header-', ''));
      const newIndex = shippingColumnOrder.indexOf(over.id.replace('shipping-header-', ''));

      if (oldIndex !== -1 && newIndex !== -1) {
        const newOrder = [...shippingColumnOrder];
        const [movedItem] = newOrder.splice(oldIndex, 1);
        newOrder.splice(newIndex, 0, movedItem);

        if (onShippingColumnDragEnd) {
          onShippingColumnDragEnd(newOrder);
        }
      }
    }
  };

  return (
    <DraggableSectionWrapper id="section5" sectionNumber={5}>
      {/* Section Header */}
      <div className="section-header" style={{
        padding: '12px',
        backgroundColor: '#f8fafc',
        borderBottom: '1px solid #e5e7eb',
        fontWeight: '600',
        fontSize: '14px',
        color: '#374151',
        textAlign: 'center'
      }}>
        Section 5 - SHIPPING DETAILS
        <span className="drag-indicator">
          ::
        </span>
      </div>

      <div
        ref={setNodeRef}
        className={`shipping-details ${isPaletteOver ? 'palette-over' : ''}`}
        data-palette-over={isPaletteOver}
        style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr 1fr 1fr',
          gap: '0',
          border: '1px solid #e5e7eb',
          borderRadius: '0 0 8px 8px',
          overflow: 'hidden',
          position: 'relative'
        }}
      >
        {/* Section Headers - Now Sortable */}
        {shippingColumnOrder.map((columnId, index) => {
          const config = columnConfig[columnId];
          const isLastColumn = index === shippingColumnOrder.length - 1;

          return (
            <SortableShippingColumnHeader 
              key={`header-${columnId}`} 
              id={`shipping-header-${columnId}`}
            >
              <div className="section-header" style={{
                padding: '12px',
                backgroundColor: '#f8fafc',
                borderBottom: '1px solid #e5e7eb',
                borderRight: isLastColumn ? 'none' : '1px solid #e5e7eb',
                fontWeight: '600',
                fontSize: '14px',
                textAlign: 'center',
                color: '#374151',
                cursor: 'grab'
              }}>
                {config.label}
              </div>
            </SortableShippingColumnHeader>
          );
        })}

        {/* Field Content - Matching Column Order */}
        {shippingColumnOrder.map((columnId, index) => {
          const config = columnConfig[columnId];
          const isLastColumn = index === shippingColumnOrder.length - 1;

          return (
            <div 
              key={`field-${columnId}`}
              className="shipping-field" 
              data-field={columnId} 
              style={{
                padding: '15px',
                borderRight: isLastColumn ? 'none' : '1px solid #e5e7eb',
                minHeight: '60px',
                display: 'flex',
                alignItems: 'center'
              }}
            >
              <span 
                className="editable-field" 
                contentEditable="true" 
                data-placeholder={config.placeholder}
                style={{
                  fontSize: '14px',
                  color: '#374151',
                  width: '100%',
                  outline: 'none'
                }}
              />
            </div>
          );
        })}

        {/* Drop Indicator */}
        {isPaletteOver && (
          <div className="drop-indicator" style={{
            position: 'absolute',
            top: '0',
            left: '0',
            right: '0',
            bottom: '0',
            backgroundColor: 'rgba(16, 185, 129, 0.1)',
            border: '2px dashed #10b981',
            borderRadius: '8px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: '5'
          }}>
            <div style={{
              backgroundColor: '#10b981',
              color: 'white',
              padding: '8px 16px',
              borderRadius: '6px',
              fontSize: '14px',
              fontWeight: '600'
            }}>
              Drop field here
            </div>
          </div>
        )}
      </div>
    </DraggableSectionWrapper>
  );
};

export default Section5Shipping;
