import React, { useState, useEffect } from 'react';

const HeaderColorPicker = () => {
  const [headerColor, setHeaderColor] = useState('#000000');
  const [textColor, setTextColor] = useState('#ffffff');
  const [isOpen, setIsOpen] = useState(false);

  // Update CSS variables when colors change
  useEffect(() => {
    document.documentElement.style.setProperty('--header-background', headerColor);
    document.documentElement.style.setProperty('--header-text', textColor);

    // Update border color based on text color with transparency
    const borderColor = textColor + '4D'; // 30% opacity
    document.documentElement.style.setProperty('--header-border', borderColor);
  }, [headerColor, textColor]);

  // Convert hex to HSL for the hue circle
  const hexToHsl = (hex) => {
    const r = parseInt(hex.slice(1, 3), 16) / 255;
    const g = parseInt(hex.slice(3, 5), 16) / 255;
    const b = parseInt(hex.slice(5, 7), 16) / 255;

    const max = Math.max(r, g, b);
    const min = Math.min(r, g, b);
    let h, s, l = (max + min) / 2;

    if (max === min) {
      h = s = 0;
    } else {
      const d = max - min;
      s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
      switch (max) {
        case r: h = (g - b) / d + (g < b ? 6 : 0); break;
        case g: h = (b - r) / d + 2; break;
        case b: h = (r - g) / d + 4; break;
        default: break;
      }
      h /= 6;
    }

    return { h: h * 360, s: s * 100, l: l * 100 };
  };

  // Convert HSL to hex
  const hslToHex = (h, s, l) => {
    h /= 360;
    s /= 100;
    l /= 100;

    const c = (1 - Math.abs(2 * l - 1)) * s;
    const x = c * (1 - Math.abs((h * 6) % 2 - 1));
    const m = l - c / 2;
    let r = 0, g = 0, b = 0;

    if (0 <= h && h < 1/6) {
      r = c; g = x; b = 0;
    } else if (1/6 <= h && h < 1/3) {
      r = x; g = c; b = 0;
    } else if (1/3 <= h && h < 1/2) {
      r = 0; g = c; b = x;
    } else if (1/2 <= h && h < 2/3) {
      r = 0; g = x; b = c;
    } else if (2/3 <= h && h < 5/6) {
      r = x; g = 0; b = c;
    } else if (5/6 <= h && h <= 1) {
      r = c; g = 0; b = x;
    }

    const rHex = Math.round((r + m) * 255).toString(16).padStart(2, '0');
    const gHex = Math.round((g + m) * 255).toString(16).padStart(2, '0');
    const bHex = Math.round((b + m) * 255).toString(16).padStart(2, '0');

    return `#${rHex}${gHex}${bHex}`;
  };

  // Handle hue circle click
  const handleHueClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const x = e.clientX - rect.left - centerX;
    const y = e.clientY - rect.top - centerY;

    const angle = Math.atan2(y, x) * (180 / Math.PI);
    const hue = angle < 0 ? angle + 360 : angle;

    const hsl = hexToHsl(headerColor);
    const newHex = hslToHex(hue, hsl.s, hsl.l);
    setHeaderColor(newHex);
  };

  // Handle saturation/lightness changes
  const handleSaturationChange = (e) => {
    const hsl = hexToHsl(headerColor);
    const newHex = hslToHex(hsl.h, e.target.value, hsl.l);
    setHeaderColor(newHex);
  };

  const handleLightnessChange = (e) => {
    const hsl = hexToHsl(headerColor);
    const newHex = hslToHex(hsl.h, hsl.s, e.target.value);
    setHeaderColor(newHex);
  };

  // Preset colors for quick selection
  const presetColors = [
    '#000000', '#1f2937', '#374151', '#4b5563', '#6b7280',
    '#1e40af', '#1d4ed8', '#2563eb', '#3b82f6', '#60a5fa',
    '#059669', '#10b981', '#34d399', '#6ee7b7', '#a7f3d0',
    '#dc2626', '#ef4444', '#f87171', '#fca5a5', '#fecaca',
    '#7c2d12', '#92400e', '#a16207', '#ca8a04', '#eab308'
  ];

  return (
    <div className="header-color-picker">
      <button 
        className="color-picker-toggle"
        onClick={() => setIsOpen(!isOpen)}
        title="Change Header Colors"
      >
        🎨 Header Colors
      </button>

      {isOpen && (
        <div className="color-picker-panel">
          <div className="color-picker-header">
            <h4>Header Color Settings</h4>
            <button 
              className="close-btn"
              onClick={() => setIsOpen(false)}
            >
              ×
            </button>
          </div>

          <div className="color-section">
            <label>Header Background:</label>
            <div className="color-inputs">
              <input
                type="color"
                value={headerColor}
                onChange={(e) => setHeaderColor(e.target.value)}
                title="Pick header background color"
              />
              <input
                type="text"
                value={headerColor}
                onChange={(e) => setHeaderColor(e.target.value)}
                placeholder="#000000"
                pattern="^#[0-9A-Fa-f]{6}$"
                title="Enter hex color code"
              />
            </div>
          </div>

          <div className="color-section">
            <label>Header Text:</label>
            <div className="color-inputs">
              <input
                type="color"
                value={textColor}
                onChange={(e) => setTextColor(e.target.value)}
                title="Pick header text color"
              />
              <input
                type="text"
                value={textColor}
                onChange={(e) => setTextColor(e.target.value)}
                placeholder="#ffffff"
                pattern="^#[0-9A-Fa-f]{6}$"
                title="Enter hex color code"
              />
            </div>
          </div>

          <div className="color-section">
            <label>Hue Circle:</label>
            <div 
              className="hue-circle"
              onClick={handleHueClick}
              title="Click to change hue"
            >
              <div className="hue-circle-inner">
                <div 
                  className="hue-indicator"
                  style={{
                    transform: `rotate(${hexToHsl(headerColor).h}deg)`
                  }}
                ></div>
              </div>
            </div>
          </div>

          <div className="color-section">
            <label>Saturation: {hexToHsl(headerColor).s.toFixed(0)}%</label>
            <input
              type="range"
              min="0"
              max="100"
              value={hexToHsl(headerColor).s}
              onChange={handleSaturationChange}
              className="slider"
            />
          </div>

          <div className="color-section">
            <label>Lightness: {hexToHsl(headerColor).l.toFixed(0)}%</label>
            <input
              type="range"
              min="0"
              max="100"
              value={hexToHsl(headerColor).l}
              onChange={handleLightnessChange}
              className="slider"
            />
          </div>

          <div className="color-section">
            <label>Preset Colors:</label>
            <div className="preset-colors">
              {presetColors.map((color, index) => (
                <button
                  key={index}
                  className="preset-color-btn"
                  style={{ backgroundColor: color }}
                  onClick={() => setHeaderColor(color)}
                  title={color}
                />
              ))}
            </div>
          </div>

          <div className="color-section">
            <label>Preview:</label>
            <div 
              className="color-preview"
              style={{ 
                backgroundColor: headerColor, 
                color: textColor 
              }}
            >
              Sample Header Text
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HeaderColorPicker;
