import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

function SortableVendorField({ field }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} className="vendor-field-item">
      <div className="drag-handle" {...attributes} {...listeners}>
        ::
      </div>
      <div className="vendor-field-content">
        <span className="vendor-field-label">{field.label}</span>
        <span 
          id={field.id} 
          className="editable-field" 
          contentEditable="true" 
          data-placeholder={field.placeholder}
          dangerouslySetInnerHTML={{ __html: field.value || field.placeholder }}
        />
      </div>
    </div>
  );
}

export default SortableVendorField;
