            </div>
          </div>

          {/* XML Generator Modal */}
          <XMLGeneratorModal
            isOpen={isXMLModalOpen}
            onClose={() => setIsXMLModalOpen(false)}
            formData={xmlFormData}
            onRefresh={() => {
              // Refresh form data if needed
              console.log('üîÑ Refreshing form data...');
            }}
          />
        </div>
      </div>
    </div>
  );
}

