import React from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy } from '@dnd-kit/sortable';
import SortableCompanyField from './SortableCompanyField';

function Section1CompanyInfo({ 
  companyFields, 
  sensors, 
  onCompanyDragEnd,
  onAddCompanyField,
  onRemoveCompanyField,
  onLabelChange
}) {
  const handleAddField = () => {
    const newField = {
      id: `company-field-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      label: 'New Field:',
      placeholder: 'Enter field value'
    };
    onAddCompanyField(newField);
  };

  return (
    <div className="company-info-section">
      <div className="section-title">Section 1</div>

      {/* Company Fields with Drag and Drop */}
      <DndContext 
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={onCompanyDragEnd}
      >
        <SortableContext 
          items={companyFields.map(field => field.id)}
          strategy={verticalListSortingStrategy}
        >
          <div className="company-fields-container">
            {companyFields.map(field => (
              <SortableCompanyField 
                key={field.id} 
                field={field} 
                onRemove={onRemoveCompanyField}
                onLabelChange={onLabelChange}
              />
            ))}
          </div>
        </SortableContext>
      </DndContext>

      {/* Add Field Button */}
      <div className="add-field-section">
        <button 
          type="button" 
          className="add-field-btn"
          onClick={handleAddField}
        >
          + Add Field
        </button>
      </div>

      {/* Change Tracking Display */}
      <div className="change-tracking-section">
        <div className="tracking-header">
          <h4>Field Tracking</h4>
          <div className="tracking-controls">
            <span className="field-count">Total Fields: {companyFields.length}</span>
            <button 
              type="button" 
              className="export-json-btn"
              onClick={() => {
                const jsonData = JSON.stringify(companyFields, null, 2);
                navigator.clipboard.writeText(jsonData);
                alert('Field data copied to clipboard!');
              }}
              title="Copy field data as JSON"
            >
              ðŸ“‹ Copy JSON
            </button>
          </div>
        </div>
        <div className="tracking-content">
          {companyFields.map((field, index) => (
            <div key={field.id} className="tracking-item">
              <span className="tracking-index">#{index + 1}</span>
              <span className="tracking-id">{field.id}</span>
              <span className="tracking-label">{field.label}</span>
              <span className="tracking-placeholder">{field.placeholder}</span>
            </div>
          ))}
        </div>

        {/* JSON Preview */}
        <div className="json-preview">
          <details>
            <summary>View Raw JSON Data</summary>
            <pre className="json-content">
              {JSON.stringify(companyFields, null, 2)}
            </pre>
          </details>
        </div>
      </div>
    </div>
  );
}

export default Section1CompanyInfo;
