import React from 'react';
import { DndContext, closestCenter, useDroppable } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import SortableVendorField from './SortableVendorField';
import SortableShipToField from './SortableShipToField';

// Draggable Section Header Component
function DraggableSectionHeader({ children, id }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style} 
      className="section-header"
      {...attributes} 
      {...listeners} 
      title="Drag to reorder sections"
    >
      {children}
      <span className="drag-indicator" style={{ 
        float: 'right', 
        opacity: 0.7, 
        fontSize: '12px',
        marginRight: '8px'
      }}>
        ::
      </span>
    </div>
  );
}

function Section3VendorShipTo({ 
  vendorFields, 
  shipToFields,
  sensors, 
  onVendorDragEnd,
  onShipToDragEnd,
  onVendorShipToSectionDragEnd,
  vendorShipToSectionOrder = ['section3', 'section4']
}) {
  return (
    <div className="vendor-shipping-section">
      {/* Section-level Drag and Drop for Vendor and Ship To */}
      <DndContext 
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={onVendorShipToSectionDragEnd}
      >
        <SortableContext 
          items={vendorShipToSectionOrder}
          strategy={verticalListSortingStrategy}
        >
          {vendorShipToSectionOrder.map(sectionId => {
            if (sectionId === 'section3') {
              return (
                <div key="section3" id="section3" className="sortable-section vendor-section">
                  {/* Vendor Section with Drag Handle */}
                  <DraggableSectionHeader id="section3">
                    VENDOR
                  </DraggableSectionHeader>

                  <DroppableVendorSection>
                    <div className="section-content">
                      <DndContext 
                        sensors={sensors}
                        collisionDetection={closestCenter}
                        onDragEnd={onVendorDragEnd}
                      >
                        <SortableContext 
                          items={vendorFields.map(field => field.id)}
                          strategy={verticalListSortingStrategy}
                        >
                          <div className="vendor-fields-container">
                            {vendorFields.map(field => (
                              <SortableVendorField key={field.id} field={field} />
                            ))}
                          </div>
                        </SortableContext>
                      </DndContext>
                    </div>
                  </DroppableVendorSection>
                </div>
              );
            } else if (sectionId === 'section4') {
              return (
                <div key="section4" id="section4" className="sortable-section ship-to-section">
                  {/* Ship To Section with Drag Handle */}
                  <DraggableSectionHeader id="section4">
                    SHIP TO
                  </DraggableSectionHeader>

                  <div className="section-content">
                    <DndContext 
                      sensors={sensors}
                      collisionDetection={closestCenter}
                      onDragEnd={onShipToDragEnd}
                    >
                      <SortableContext 
                        items={shipToFields.map(field => field.id)}
                        strategy={verticalListSortingStrategy}
                      >
                        <div className="ship-to-fields-container">
                          {shipToFields.map(field => (
                            <SortableShipToField key={field.id} field={field} />
                          ))}
                        </div>
                      </SortableContext>
                    </DndContext>
                  </div>
                </div>
              );
            }
            return null;
          })}
        </SortableContext>
      </DndContext>
    </div>
  );
}

export default Section3VendorShipTo;
