// AI provider hook - uses ChatGPT API directly
// Set REACT_APP_OPENAI_API_KEY in your .env file to keep the key secure

export function useAIProvider() {
  const apiKey = process.env.REACT_APP_OPENAI_API_KEY;

  async function getFieldSuggestions(payload) {
    if (!apiKey) {
      throw new Error('OpenAI API key not configured. Set REACT_APP_OPENAI_API_KEY in your .env file');
    }

    try {
      // Build reusable messages for both chat and fallback calls
      const messages = [
        {
          role: 'system',
          content: `You are a helpful assistant that fills out purchase order forms. 
              Analyze the provided fields and return realistic, business-appropriate values.
              Return ONLY a JSON object with field IDs as keys and suggested values as strings.
              Example: {"company-name": "Acme Corporation", "po-number": "PO-2024-001"}`
        },
        {
          role: 'user',
          content: `Please fill out these purchase order fields with realistic business data:
              Company Fields: ${payload.companyFields?.map(f => `${f.id}: ${f.label}`).join(', ')}
              Purchase Order Fields: ${payload.purchaseOrderFields?.map(f => `${f.id}: ${f.label}`).join(', ')}
              Vendor Fields: ${payload.vendorFields?.map(f => `${f.id}: ${f.label}`).join(', ')}
              Ship To Fields: ${payload.shipToFields?.map(f => `${f.id}: ${f.label}`).join(', ')}

              Return only valid JSON with field IDs as keys.`
        }
      ];

      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: 'gpt-5',
          messages,
          temperature: 1,
          max_completion_tokens: 1000
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`OpenAI API error: ${errorData.error?.message || response.statusText}`);
      }

      const data = await response.json();
      console.debug('ðŸ§ª OpenAI chat data:', data);

      // Extract content across possible API response shapes
      const extractContent = (payload) => {
        const cc = payload?.choices?.[0]?.message?.content;
        if (cc) return cc;
        if (typeof payload?.output_text === 'string' && payload.output_text.trim()) {
          return payload.output_text;
        }
        const outputArray = payload?.output;
        if (Array.isArray(outputArray) && outputArray.length > 0) {
          const parts = outputArray[0]?.content;
          if (Array.isArray(parts)) {
            const textPart = parts.find(p => typeof p?.text === 'string');
            if (textPart?.text) return textPart.text;
          }
        }
        const topContent = payload?.content;
        if (Array.isArray(topContent)) {
          const textPart = topContent.find(p => typeof p?.text === 'string');
          if (textPart?.text) return textPart.text;
        }
        return undefined;
      };

      let content = extractContent(data);
      console.debug('ðŸ§ª Extracted content after chat:', typeof content, content?.slice ? content.slice(0, 200) : content);

      if (!content) {
        // Fallback to Responses API once
        const combinedPrompt = `${messages[0].content}\n\n${messages[1].content}`;
        const respFallback = await fetch('https://api.openai.com/v1/responses', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${apiKey}`,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            model: 'gpt-5',
            input: combinedPrompt,
            max_output_tokens: 1000
          })
        });

        if (!respFallback.ok) {
          const errorData = await respFallback.json();
          throw new Error(`OpenAI API error: ${errorData.error?.message || respFallback.statusText}`);
        }

        const dataFallback = await respFallback.json();
        console.debug('ðŸ§ª OpenAI responses data (fallback):', dataFallback);
        content = extractContent(dataFallback);
        console.debug('ðŸ§ª Extracted content after fallback:', typeof content, content?.slice ? content.slice(0, 200) : content);
      }

      if (!content) {
        throw new Error('No response content from OpenAI');
      }

      // Parse the JSON response, tolerate code fences or extra text, and filter to known field IDs
      try {
        // Remove possible Markdown code fences
        let cleaned = content.trim();
        if (cleaned.startsWith('```')) {
          cleaned = cleaned.replace(/^```(?:json)?\s*/i, '').replace(/```$/i, '').trim();
        }

        let parsed;
        try {
          parsed = JSON.parse(cleaned);
        } catch (innerErr) {
          // Try extracting the first JSON object if extra prose is present
          const firstBrace = cleaned.indexOf('{');
          const lastBrace = cleaned.lastIndexOf('}');
          if (firstBrace !== -1 && lastBrace !== -1 && lastBrace > firstBrace) {
            const maybeJson = cleaned.slice(firstBrace, lastBrace + 1);
            parsed = JSON.parse(maybeJson);
          } else {
            throw innerErr;
          }
        }

        // Build allowlist of known field IDs and a lookup by normalized label
        const allFields = [
          ...(payload.companyFields || []),
          ...(payload.purchaseOrderFields || []),
          ...(payload.vendorFields || []),
          ...(payload.shipToFields || [])
        ];
        const allowedIds = allFields.map(f => f.id);
        const normalize = (s) => String(s || '')
          .toLowerCase()
          .replace(/[`"'â€™]/g, '')
          .replace(/\s+/g, ' ')
          .trim()
          .replace(/[^a-z0-9]+/g, '');
        const labelToId = Object.fromEntries(
          allFields.map(f => [normalize(f.label || f.id), f.id])
        );

        const coerceToString = (v) => {
          if (typeof v === 'string') return v;
          if (typeof v === 'number' || typeof v === 'boolean') return String(v);
          if (v && typeof v === 'object') {
            if (typeof v.value === 'string') return v.value;
            if (typeof v.text === 'string') return v.text;
          }
          return undefined;
        };

        const suggestions = {};
        for (const [rawKey, rawVal] of Object.entries(parsed || {})) {
          const value = coerceToString(rawVal);
          if (value === undefined) continue;

          // direct id match
          if (allowedIds.includes(rawKey)) {
            suggestions[rawKey] = value;
            continue;
          }

          // label-based or fuzzy match
          const normalizedKey = normalize(rawKey);
          const maybeId = labelToId[normalizedKey];
          if (maybeId && allowedIds.includes(maybeId)) {
            suggestions[maybeId] = value;
          }
        }

        return { suggestions };
      } catch (parseError) {
        console.error('Failed to parse OpenAI response:', content);
        throw new Error('Invalid JSON response from AI - please try again');
      }
    } catch (error) {
      console.error('OpenAI API call failed:', error);
      throw error;
    }
  }

  return { getFieldSuggestions };
}
