          }}
          style={{
            backgroundColor: '#4ecdc4',
            color: 'white',
            border: 'none',
            padding: '5px 10px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '10px'
          }}
        >
          Log State
        </button>

        <button 
          onClick={() => {
            console.log('ğŸ§ª Testing section swap...');
            setSections1And2Order(prev => {
              const newOrder = [...prev].reverse();
              console.log('ğŸ”„ Manual swap:', prev, 'â†’', newOrder);
              updateDebugPanel('MANUAL SWAP', `${prev.join('â†’')} â†’ ${newOrder.join('â†’')}`);
              return newOrder;
            });
          }}
          style={{
            backgroundColor: '#ff6b6b',
            color: 'white',
            border: 'none',
            padding: '5px 10px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '10px',
            marginLeft: '5px'
          }}
        >
          Test Swap
        </button>

        <button 
          onClick={() => {
            console.log('ğŸ§ª Testing drag simulation...');
            // Simulate a drag start event
            const mockEvent = {
              active: { id: 'section1', data: { current: { type: 'section', sectionNumber: 1 } } },
              over: { id: 'section2', data: { current: { type: 'section', sectionNumber: 2 } } }
            };
            console.log('ğŸ§ª Simulating drag end event:', mockEvent);
            handleSectionDragEnd(mockEvent);
          }}
          style={{
            backgroundColor: '#ffa500',
            color: 'white',
            border: 'none',
            padding: '5px 10px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '10px',
            marginLeft: '5px'
          }}
        >
          Test Drag
        </button>

        {/* Simple DND Test */}
        <div style={{ marginTop: '15px', padding: '10px', border: '1px solid #666', borderRadius: '4px' }}>
          <h5 style={{ margin: '0 0 8px 0', color: '#4ecdc4' }}>ğŸ§ª DND Test</h5>
          <div style={{ fontSize: '10px', marginBottom: '5px' }}>
            Try dragging this test item:
          </div>
          <div 
            style={{
              width: '100%',
              height: '30px',
              backgroundColor: '#7c3aed',
              color: 'white',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              cursor: 'grab',
              borderRadius: '4px',
              fontSize: '10px'
            }}
            draggable="true"
            onDragStart={(e) => {
              console.log('ğŸ§ª Test drag start:', e);
              e.dataTransfer.setData('text/plain', 'test');
            }}
            onDragOver={(e) => {
              e.preventDefault();
              console.log('ğŸ§ª Test drag over:', e);
            }}
            onDrop={(e) => {
              e.preventDefault();
              console.log('ğŸ§ª Test drop:', e);
            }}
          >
            ğŸ§ª Test Drag Item
          </div>
        </div>
      </div>

      {/* Real-time Status Indicator removed per request */}

      {/* Purchase Order Form */}
      <div className="container">
        <div className="form-content">
          {/* Header Section */}
          <div className="section-container">
            <div className="header-section vendor-shipping-columns">
              {/* Section-level Drag and Drop */}
              <SortableContext 
                items={sections1And2Order}
                strategy={horizontalListSortingStrategy}
              >
                {console.log('ğŸ”§ SortableContext for sections 1&2:', { 
                  items: sections1And2Order, 
                  strategy: 'horizontalListSortingStrategy',
                  sections1And2OrderType: typeof sections1And2Order,
                  sections1And2OrderLength: sections1And2Order?.length,
                  itemsArray: Array.isArray(sections1And2Order) ? sections1And2Order : 'NOT ARRAY',
                  timestamp: new Date().toISOString()
                })}
                {console.log('ğŸ”§ SortableContext items validation:', {
                  itemsValid: Array.isArray(sections1And2Order) && sections1And2Order.length > 0,
                  itemsContent: sections1And2Order,
                  strategyValid: horizontalListSortingStrategy !== undefined,
                  timestamp: new Date().toISOString()
                })}
                {/* CRITICAL: Render sections dynamically based on sections1And2Order state */}
                {sections1And2Order.map((sectionId, index) => {
                  console.log(`ğŸ”§ Rendering section: ${sectionId} at index ${index}`);

                  if (sectionId === 'section1') {
                    return (
                      <DroppableZone key="section1" id="drop-company" data-zone-label="Company Info">
                        <DraggableSectionWrapper id="section1" sectionNumber="1" isSectionHandleDragging={isSectionHandleDragging} showDragHandle={true}>
                          <Section1CompanyInfo 
                            companyFields={companyFields}
                            sensors={sensors}
                            onCompanyDragEnd={handleCompanyDragEnd}
                            onAddCompanyField={handleAddCompanyField}
                            onRemoveCompanyField={handleRemoveCompanyField}
                            onLabelChange={handleCompanyFieldLabelChange}
                            onContentChange={handleContentChange}
                            lastModified={lastModified}
                          />
                        </DraggableSectionWrapper>
                      </DroppableZone>
                    );
                  } else if (sectionId === 'section2') {
                    return (
                      <DroppableZone key="section2" id="drop-po" data-zone-label="Purchase Order">
                        <DraggableSectionWrapper id="section2" sectionNumber="2" isSectionHandleDragging={isSectionHandleDragging} showDragHandle={true}>
                          <Section2PurchaseOrder 
                            purchaseOrderFields={purchaseOrderFields}
                            sensors={sensors}
                            onPurchaseOrderDragEnd={handlePurchaseOrderDragEnd}
                            onAddPurchaseOrderField={handleAddPurchaseOrderField}
                            onRemovePurchaseOrderField={handleRemovePurchaseOrderField}
                            onLabelChange={handlePurchaseOrderFieldLabelChange}
                            onContentChange={handleContentChange}
                            lastModified={poLastModified}
                          />
                        </DraggableSectionWrapper>
                      </DroppableZone>
                    );
                  }
                  return null;
                })}
              </SortableContext>
            </div>
          </div>

          {/* Vendor-ShipTo and Shipping Details Sections (Vertically Swappable) */}
          <SortableContext 
            items={vendorShipToAndShippingDetailsOrder}
            strategy={verticalListSortingStrategy}
          >
            {vendorShipToAndShippingDetailsOrder.map(sectionId => {
              if (sectionId === 'sections3And4') {
                return (
                  /* Vendor and Ship To Sections - Side by Side */
                  <div key="sections3And4" className="vendor-shipping-columns">
                    <SortableContext 
                      items={vendorShipToSectionOrder}
                      strategy={horizontalListSortingStrategy}
                    >
                      {vendorShipToSectionOrder.map(innerSectionId => {
                        if (innerSectionId === 'section3') {
                          return (
                            <DroppableZone key="section3" id="drop-vendor">
                              <DraggableSectionWrapper id="section3" sectionNumber="3" isSectionHandleDragging={isSectionHandleDragging} showDragHandle={false}>
                                <Section3Vendor 
                                  vendorFields={vendorFields}
                                  sensors={sensors}
                                  onVendorDragEnd={handleVendorDragEnd}
                                  onAddVendorField={handleAddVendorField}
                                  onRemoveVendorField={handleRemoveVendorField}
                                  onLabelChange={handleVendorFieldLabelChange}
                                  onContentChange={handleContentChange}
                                  lastModified={Date.now()}
                                />
                              </DraggableSectionWrapper>
                            </DroppableZone>
                          );
                        } else if (innerSectionId === 'section4') {
                          return (
                            <DroppableZone key="section4" id="drop-ship-to">
                              <DraggableSectionWrapper id="section4" sectionNumber="4" isSectionHandleDragging={isSectionHandleDragging} showDragHandle={false}>
                                <Section4ShipTo 
                                  shipToFields={shipToFields}
                                  sensors={sensors}
                                  onShipToDragEnd={handleShipToDragEnd}
                                  onAddShipToField={handleAddShipToField}
                                  onRemoveShipToField={handleRemoveShipToField}
                                  onLabelChange={handleShipToFieldLabelChange}
                                  onContentChange={handleContentChange}
                                  lastModified={Date.now()}
                                />
                              </DraggableSectionWrapper>
                            </DroppableZone>
                          );
                        }
                        return null;
                      })}
                    </SortableContext>
                  </div>
                );
              } else if (sectionId === 'sections5And6') {
                return (
                  <DraggableSectionWrapper key="sections5And6" id="sections5And6" sectionNumber="5&6">
                    {/* Shipping Details Section */}
                    <div className="shipping-details">
                      <div className="section-header">REQUISITIONER</div>
                      <div className="section-header">SHIP VIA</div>
                      <div className="section-header">F.O.B.</div>
                      <div className="section-header">SHIPPING TERMS</div>
                      <div className="shipping-field" data-field="requisitioner"><span className="editable-field" contentEditable="true" data-placeholder="Requisitioner name" /></div>
                      <div className="shipping-field" data-field="shipVia"><span className="editable-field" contentEditable="true" data-placeholder="Shipping method" /></div>
                      <div className="shipping-field" data-field="fob"><span className="editable-field" contentEditable="true" data-placeholder="FOB terms" /></div>
                      <div className="shipping-field" data-field="shippingTerms"><span className="editable-field" contentEditable="true" data-placeholder="Shipping terms" /></div>
                    </div>
                  </DraggableSectionWrapper>
                );
              }
              return null;
            })}
          </SortableContext>

          {/* Items Table */}
          <SortableContext items={lineItemColumnOrder} strategy={verticalListSortingStrategy}>
            <table className="itemtable">
              <thead>
                <tr>
                  <th></th>
                  {lineItemColumnOrder.map((columnId) => {
                    const columnConfig = {
                      itemNumber: { label: 'Item#', colSpan: 3 },
                      description: { label: 'Description', colSpan: 12 },
                      qty: { label: 'Qty', colSpan: 2 },
                      rate: { label: 'Rate', colSpan: 3 },
                      amount: { label: 'Amount', colSpan: 3 }
                    };

                    const config = columnConfig[columnId];
                    return (
                      <th key={columnId} colSpan={config.colSpan}>
                        {config.label}
                      </th>
                    );
                  })}
                </tr>
              </thead>
              <tbody>
                {[1, 2, 3, 4, 5].map((row) => (
                  <tr key={row} className="draggable-row">
                    <td></td>
                    {lineItemColumnOrder.map((columnId) => {
                      const columnConfig = {
                        itemNumber: { colSpan: 3, placeholder: 'Item number' },
                        description: { colSpan: 12, placeholder: 'Item description' },
                        qty: { colSpan: 2, placeholder: 'Qty' },
                        rate: { colSpan: 3, placeholder: '$0.00' },
                        amount: { colSpan: 3, placeholder: '$0.00' }
                      };

                      const config = columnConfig[columnId];
                      return (
                        <td key={`${row}-${columnId}`} colSpan={config.colSpan} data-column={columnId}>
                          <span className="editable-field" contentEditable="true" data-placeholder={config.placeholder} />
                        </td>
                      );
                    })}
                  </tr>
                ))}
              </tbody>
            </table>
          </SortableContext>

          {/* Comments and Totals - Sections 8 & 9 */}
          <SortableContext items={commentsTotalsSectionOrder} strategy={horizontalListSortingStrategy}>
            <div className="comments-totals-section" style={{ display: 'flex', gap: '20px' }}>
              {commentsTotalsSectionOrder.map((sectionId) => {
                if (sectionId === 'section8') {
                  return (
                    <div key="section8" id="section8" className="sortable-section comments-section" style={{ flex: 1 }}>
                      <DraggableSectionHeader id="section8">
                        Comments or Special Instructions ::
                      </DraggableSectionHeader>
                      <div className="section-content">
                        <div className="comments-content">
                          <span className="editable-field" contentEditable="true" data-placeholder="Enter comments or special instructions..." />
                        </div>
                      </div>
                    </div>
                  );
                } else if (sectionId === 'section9') {
                  return (
                    <div key="section9" id="section9" className="sortable-section totals-section" style={{ flex: 1 }}>
                      <DraggableSectionHeader id="section9">
                        Totals ::
                      </DraggableSectionHeader>
                      <div className="section-content">
                        <div className="total-row" data-field="subtotal">
                          <span>SUBTOTAL:</span>
                          <span className="calculated-field">$0.00</span>
                        </div>
                        <div className="total-row" data-field="tax">
                          <span>TAX:</span>
                          <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                        </div>
                        <div className="total-row" data-field="shipping">
                          <span>SHIPPING:</span>
                          <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                        </div>
                        <div className="total-row" data-field="other">
                          <span>OTHER:</span>
                          <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                        </div>
                        <div className="total-row total-final" data-field="total">
                          <span>TOTAL:</span>
                          <span className="calculated-field total-field">$0.00</span>
                        </div>
                      </div>
                    </div>
                  );
                }
                return null;
              })}
            </div>
          </SortableContext>



          {/* Contact Information */}
          <div className="contact-section">
            <div className="contact-content">
              <strong>Contact Information:</strong>
              <span className="editable-field" contentEditable="true" data-placeholder="Enter contact information here" />
            </div>
          </div>

          {/* Action Buttons */}
          <div className="button-section">
            <button className="btn preview-btn" onClick={togglePreviewMode}>
              {isPreviewMode ? 'ğŸ‘ï¸ Hide Preview' : 'ğŸ‘ï¸ Show Preview'}
            </button>
            <button className="btn" onClick={handleAIFill}>
              ğŸ¤– AI Fill
            </button>
            <button className="btn export-btn" onClick={exportToXML}>
              ğŸ“„ Generate XML
            </button>
          </div>

          {/* Change History Display */}
          {changeHistory.length > 0 && (
            <div className="change-history-section">
              <div className="section-header">
                ğŸ“ Change History ({changeHistory.length} changes)
                <button className="clear-history-btn" onClick={clearChangeHistory}>
                  ğŸ—‘ï¸ Clear
                </button>
              </div>
              <div className="change-list">
                {changeHistory.slice(0, 10).map((change) => (
                  <div key={change.id} className="change-item">
                    <span className="change-time">
                      {new Date(change.timestamp).toLocaleTimeString()}
                    </span>
                    <span className="change-type">{change.type}</span>
                    <span className="change-field">{change.fieldId}</span>
                    <span className="change-old">{change.oldValue || 'empty'}</span>
                    <span className="change-arrow">â†’</span>
                    <span className="change-new">{change.newValue || 'empty'}</span>
                  </div>
                ))}
                {changeHistory.length > 10 && (
                  <div className="change-more">
                    ... and {changeHistory.length - 10} more changes
                  </div>
                )}
              </div>
            </div>
          )}

          {/* XML Output Modal */}
          {showXMLModal && (
            <div className="xml-modal-overlay" onClick={closeXMLModal}>
              <div className="xml-modal" onClick={(e) => e.stopPropagation()}>
                <div className="xml-modal-header">
                  <h3>ğŸ“„ Generated XML Output</h3>
                  <button className="xml-modal-close" onClick={closeXMLModal}>Ã—</button>
                </div>
                <div className="xml-modal-content">
                  <div className="xml-output-container">
                    <pre className="xml-code">{xmlOutput}</pre>
                  </div>
                </div>
                <div className="xml-modal-actions">
                  <button className="btn copy-btn" onClick={copyXMLToClipboard}>
                    ğŸ“‹ Copy XML
                  </button>
                  <button className="btn download-btn" onClick={downloadXML}>
                    ğŸ“¥ Download XML
                  </button>
                  <button className="btn close-btn" onClick={closeXMLModal}>
                    Close
                  </button>
                </div>
              </div>
            </div>
          )}





        </div>
      </div>
      </DndContext>

      {/* Notification Container */}
      <div id="notification-container"></div>
    </div>
  );
}

export default PurchaseOrderForm;
