  const generateUniqueFieldId = (baseLabel, existingIds) => {
    const slugBase = String(baseLabel)
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/(^-|-$)/g, '');
    let candidate = slugBase || 'field';
    let counter = 1;
    while (existingIds.includes(candidate)) {
      candidate = `${slugBase}-${counter++}`;
    }
    return candidate;
  };

