import React, { useState } from 'react';
import './PurchaseOrderForm.css';

// Import modular components
import Section1CompanyInfo from './components/Section1CompanyInfo';
import Section2PurchaseOrder from './components/Section2PurchaseOrder';
import Section3VendorShipTo from './components/Section3VendorShipTo';

// Import custom hooks
import { 
  useDragAndDropSensors, 
  useCompanyFieldsDragEnd, 
  usePurchaseOrderFieldsDragEnd,
  useVendorFieldsDragEnd,
  useShipToFieldsDragEnd
} from './hooks/useDragAndDrop';

import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy } from '@dnd-kit/sortable';


// Main Purchase Order Form Component

// Main Purchase Order Form Component
function PurchaseOrderForm() {
  // ============================================================================
  // STATE MANAGEMENT
  // ============================================================================

  // Company fields state for Section 1
  const [companyFields, setCompanyFields] = useState([
    { id: 'company-name', label: 'Company Name:', placeholder: 'Enter company name' },
    { id: 'company-address', label: 'Street Address:', placeholder: 'Enter street address' },
    { id: 'company-city-state', label: 'City, ST ZIP:', placeholder: 'City, State ZIP' },
    { id: 'company-phone', label: 'Phone:', placeholder: '(555) 123-4567' },
    { id: 'company-fax', label: 'Fax:', placeholder: '(555) 123-4567' },
    { id: 'company-website', label: 'Website:', placeholder: 'www.example.com' }
  ]);

  // Track last modification time for company fields
  const [lastModified, setLastModified] = useState(new Date().toISOString());

  // Track last modification time for purchase order fields
  const [poLastModified, setPoLastModified] = useState(new Date().toISOString());

  // Purchase Order fields state for Section 2
  const [purchaseOrderFields, setPurchaseOrderFields] = useState([
    { id: 'po-title', label: 'Purchase Order', placeholder: '', isTitle: true },
    { id: 'po-date', label: 'DATE:', placeholder: 'MM/DD/YYYY' },
    { id: 'po-number', label: 'PO #:', placeholder: 'PO#123456' }
  ]);

  // Vendor fields state for Section 3
  const [vendorFields, setVendorFields] = useState([
    { id: 'vendor-company', label: 'Company:', placeholder: 'Vendor name' },
    { id: 'vendor-contact', label: 'Contact:', placeholder: 'Contact person' },
    { id: 'vendor-address', label: 'Address:', placeholder: 'Street address' },
    { id: 'vendor-city-state', label: 'City/State:', placeholder: 'City, ST ZIP' },
    { id: 'vendor-phone', label: 'Phone:', placeholder: '(555) 123-4567' }
  ]);

  // Ship To fields state for Section 3
  const [shipToFields, setShipToFields] = useState([
    { id: 'ship-to-name', label: 'Name:', placeholder: 'Contact name' },
    { id: 'ship-to-company', label: 'Company:', placeholder: 'Shipping company' },
    { id: 'ship-to-address', label: 'Address:', placeholder: 'Street address' },
    { id: 'ship-to-city-state', label: 'City/State:', placeholder: 'City, ST ZIP' },
    { id: 'ship-to-phone', label: 'Phone:', placeholder: '(555) 123-4567' }
  ]);

  // ============================================================================
  // CUSTOM HOOKS
  // ============================================================================

  const sensors = useDragAndDropSensors();
  const handleVendorDragEnd = useVendorFieldsDragEnd(setVendorFields);
  const handleShipToDragEnd = useShipToFieldsDragEnd(setShipToFields);

  // ============================================================================
  // COMPANY FIELD MANAGEMENT FUNCTIONS
  // ============================================================================

  // Helper function to update timestamp when company fields change
  const updateCompanyFieldsTimestamp = () => {
    setLastModified(new Date().toISOString());
  };

  // Add new company field
  const handleAddCompanyField = (newField) => {
    setCompanyFields(prevFields => [...prevFields, newField]);
    updateCompanyFieldsTimestamp();
    console.log('âž• Added new company field:', newField);
  };

  // Remove company field
  const handleRemoveCompanyField = (fieldId) => {
    setCompanyFields(prevFields => {
      const fieldToRemove = prevFields.find(field => field.id === fieldId);
      console.log('ðŸ—‘ï¸ Removed company field:', fieldToRemove);
      return prevFields.filter(field => field.id !== fieldId);
    });
    updateCompanyFieldsTimestamp();
  };

  // Update company field label
  const handleCompanyFieldLabelChange = (fieldId, newLabel) => {
    setCompanyFields(prevFields => 
      prevFields.map(field => 
        field.id === fieldId ? { ...field, label: newLabel } : field
      )
    );
    updateCompanyFieldsTimestamp();
    console.log('âœï¸ Updated company field label:', { fieldId, newLabel });
  };

  // Handle company field drag and drop with timestamp update
  const handleCompanyDragEnd = (event) => {
    useCompanyFieldsDragEnd(setCompanyFields)(event);
    updateCompanyFieldsTimestamp();
    // Only log significant reordering, not every small drag
    if (event.active.id !== event.over.id) {
      console.log('ðŸ”„ Company fields reordered');
    }
  };

  // ============================================================================
  // SUMMARY LOGGING FUNCTION
  // ============================================================================

  // Function to log a summary of all field states
  const logFieldSummary = () => {
    console.log('ðŸ“Š FIELD SUMMARY:', {
      company: {
        total: companyFields.length,
        lastModified: new Date(lastModified).toLocaleString(),
        fields: companyFields.map(f => ({ id: f.id, label: f.label }))
      },
      purchaseOrder: {
        total: purchaseOrderFields.length,
        lastModified: new Date(poLastModified).toLocaleString(),
        fields: purchaseOrderFields.map(f => ({ id: f.id, label: f.label, isTitle: f.isTitle }))
      }
    });
  };

  // Log summary on component mount
  React.useEffect(() => {
    logFieldSummary();
  }, []); // Only run once on mount

  // ============================================================================
  // PURCHASE ORDER FIELD MANAGEMENT FUNCTIONS
  // ============================================================================

  // Helper function to update timestamp when purchase order fields change
  const updatePurchaseOrderFieldsTimestamp = () => {
    setPoLastModified(new Date().toISOString());
  };

  // Add new purchase order field
  const handleAddPurchaseOrderField = (newField) => {
    setPurchaseOrderFields(prevFields => [...prevFields, newField]);
    updatePurchaseOrderFieldsTimestamp();
    console.log('âž• Added new purchase order field:', newField);
  };

  // Remove purchase order field
  const handleRemovePurchaseOrderField = (fieldId) => {
    setPurchaseOrderFields(prevFields => {
      const fieldToRemove = prevFields.find(field => field.id === fieldId);
      console.log('ðŸ—‘ï¸ Removed purchase order field:', fieldToRemove);
      return prevFields.filter(field => field.id !== fieldId);
    });
    updatePurchaseOrderFieldsTimestamp();
  };

  // Update purchase order field label
  const handlePurchaseOrderFieldLabelChange = (fieldId, newLabel) => {
    setPurchaseOrderFields(prevFields => 
      prevFields.map(field => 
        field.id === fieldId ? { ...field, label: newLabel } : field
      )
    );
    updatePurchaseOrderFieldsTimestamp();
    console.log('âœï¸ Updated purchase order field label:', { fieldId, newLabel });
  };

  // Handle purchase order field drag and drop with timestamp update
  const handlePurchaseOrderDragEnd = (event) => {
    usePurchaseOrderFieldsDragEnd(setPurchaseOrderFields)(event);
    updatePurchaseOrderFieldsTimestamp();
    // Only log significant reordering, not every small drag
    if (event.active.id !== event.over.id) {
      console.log('ðŸ”„ Purchase order fields reordered');
    }
  };

  // ============================================================================
  // SECTION-LEVEL DRAG AND DROP
  // ============================================================================

  // State for section order
  const [sectionOrder, setSectionOrder] = useState(['section1', 'section2']);

  // Function to handle section reordering
  const handleSectionDragEnd = (event) => {
    const { active, over } = event;

    if (active.id !== over.id) {
      setSectionOrder(prevOrder => {
        const oldIndex = prevOrder.indexOf(active.id);
        const newIndex = prevOrder.indexOf(over.id);

        const newOrder = [...prevOrder];
        newOrder.splice(oldIndex, 1);
        newOrder.splice(newIndex, 0, active.id);

        // Swap the actual field data when sections are reordered
        if (active.id === 'section1' && over.id === 'section2') {
          // Section 1 moved to position 2
          const companyFieldValues = companyFields.map(field => {
            const element = document.getElementById(field.id);
            return {
              ...field,
              value: element ? element.textContent.trim() : ''
            };
          });

          const purchaseOrderFieldValues = purchaseOrderFields.map(field => {
            const element = document.getElementById(field.id);
            return {
              ...field,
              isTitle: field.isTitle, // Explicitly preserve isTitle property
              value: element ? element.textContent.trim() : ''
            };
          });

          console.log('ðŸ” Before swap - Purchase Order fields:', purchaseOrderFieldValues);
          console.log('ðŸ” Title field check:', purchaseOrderFieldValues.find(f => f.isTitle));

          // Swap the fields
          setCompanyFields(purchaseOrderFieldValues);
          setPurchaseOrderFields(companyFieldValues);

          // Update timestamps
          setLastModified(new Date().toISOString());
          setPoLastModified(new Date().toISOString());

          console.log('ðŸ”„ Sections swapped via drag & drop!');
          console.log('ðŸ“Š New Section 1 (was Section 2):', purchaseOrderFieldValues);
          console.log('ðŸ“Š New Section 2 (was Section 1):', companyFieldValues);
        } else if (active.id === 'section2' && over.id === 'section1') {
          // Section 2 moved to position 1
          const companyFieldValues = companyFields.map(field => {
            const element = document.getElementById(field.id);
            return {
              ...field,
              value: element ? element.textContent.trim() : ''
            };
          });

          const purchaseOrderFieldValues = purchaseOrderFields.map(field => {
            const element = document.getElementById(field.id);
            return {
              ...field,
              isTitle: field.isTitle, // Explicitly preserve isTitle property
              value: element ? element.textContent.trim() : ''
            };
          });

          console.log('ðŸ” Before swap - Purchase Order fields:', purchaseOrderFieldValues);
          console.log('ðŸ” Title field check:', purchaseOrderFieldValues.find(f => f.isTitle));

          // Swap the fields
          setCompanyFields(purchaseOrderFieldValues);
          setPurchaseOrderFields(companyFieldValues);

          // Update timestamps
          setLastModified(new Date().toISOString());
          setPoLastModified(new Date().toISOString());

          console.log('ðŸ”„ Sections swapped via drag & drop!');
          console.log('ðŸ“Š New Section 1 (was Section 2):', purchaseOrderFieldValues);
          console.log('ðŸ“Š New Section 2 (was Section 1):', companyFieldValues);
        }

        return newOrder;
      });
    }
  };

  // ============================================================================
  // SECTION SWAPPING FUNCTION
  // ============================================================================

  // Function to swap Section 1 and Section 2
  const handleSwapSections = () => {
    // Store current values before swapping
    const companyFieldValues = companyFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        value: element ? element.textContent.trim() : ''
      };
    });

    const purchaseOrderFieldValues = purchaseOrderFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        value: element ? element.textContent.trim() : ''
      };
    });

    // Swap the fields
    setCompanyFields(purchaseOrderFieldValues);
    setPurchaseOrderFields(companyFieldValues);

    // Update timestamps
    setLastModified(new Date().toISOString());
    setPoLastModified(new Date().toISOString());

    console.log('ðŸ”„ Sections swapped successfully!');
    console.log('ðŸ“Š New Section 1 (was Section 2):', purchaseOrderFieldValues);
    console.log('ðŸ“Š New Section 2 (was Section 1):', companyFieldValues);

    // Log the swap operation
    console.log('ðŸ”„ Section Swap Details:', {
      'Section 1 â†’ Section 2': companyFieldValues.map(f => ({ id: f.id, label: f.label, value: f.value })),
      'Section 2 â†’ Section 1': purchaseOrderFieldValues.map(f => ({ id: f.id, label: f.label, value: f.value }))
    });
  };

  // ============================================================================
  // HTML EXPORT FUNCTION
  // ============================================================================

  // Function to export current page state to HTML
  const handleExportToHTML = () => {
    // Get current field values from DOM
    const getFieldValues = (fields) => {
      return fields.map(field => {
        const element = document.getElementById(field.id);
        return {
          ...field,
          value: element ? element.textContent.trim() : ''
        };
      });
    };

    const companyFieldValues = getFieldValues(companyFields);
    const purchaseOrderFieldValues = getFieldValues(purchaseOrderFields);

    // Generate HTML content
    const htmlContent = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Purchase Order - Exported</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 0; padding: 0; background-color: #f5f5f5; }
        .purchase-order-container { font-family: Arial, sans-serif; margin: 0; padding: 0; background-color: #f5f5f5; }
        .container { max-width: 1200px; margin: 0 auto; background: white; box-shadow: 0 0 20px rgba(0,0,0,0.1); border-radius: 8px; }
        .form-content { padding: 40px 50px; }
        .header-section { display: flex; gap: 40px; margin-bottom: 40px; min-height: 200px; }
        .company-info-section, .purchase-order-section { flex: 1; }
        .section-title { background: #333333; color: white; padding: 8px 12px; font-weight: bold; font-size: 14px; text-align: center; margin-bottom: 15px; border-radius: 4px; }
        .company-fields-container, .po-fields-container { display: flex; flex-direction: column; gap: 12px; }
        .field-item, .po-field-item { display: flex; align-items: center; gap: 10px; padding: 12px; border-radius: 4px; background: #f8fafc; border: 1px solid #e2e8f0; }
        .field-content, .po-field-content { display: flex; align-items: center; gap: 8px; width: 100%; }
        .field-label, .po-field-label { font-weight: normal; font-size: 14px; min-width: 80px; color: #333333; }
        .editable-field { color: #64748b; font-style: italic; }
        .title-field { font-size: 32px; font-weight: bold; color: #000000; text-align: center; display: block; width: 100%; margin-bottom: 10px; }
        .vendor-shipping-section { display: flex; gap: 40px; margin-bottom: 40px; }
        .vendor-section, .ship-to-section { flex: 1; border: 1px solid #e5e7eb; }
        .section-header { background: #333333; color: white; padding: 8px 12px; font-weight: bold; font-size: 14px; text-align: center; }
        .section-content { padding: 15px; height: 300px; padding-bottom: 40px; }
        .field-row { font-size: 14px; line-height: 1.3; color: #333333; margin-bottom: 3px; }
        .shipping-details { display: grid; grid-template-columns: 1fr 1fr 1fr 1fr; border: 1px solid #e5e7eb; margin: 30px 0; }
        .shipping-field { padding: 15px; height: 60px; border-right: 1px solid #e5e7eb; display: flex; align-items: center; }
        .shipping-field:last-child { border-right: none; }
        .itemtable { width: 100%; margin: 40px 0; border: 1px solid #e5e7eb; border-collapse: collapse; }
        .itemtable th { background: #333333; color: white; padding: 12px; font-weight: bold; text-align: center; font-size: 14px; border: 1px solid #e5e7eb; }
        .itemtable td { padding: 10px; border-bottom: 1px solid #e5e7eb; border-right: 1px solid #e5e7eb; font-size: 14px; color: #333333; }
        .comments-totals-section { display: flex; gap: 25px; margin: 30px 0; }
        .comments-section { flex: 2; border: 1px solid #e5e7eb; }
        .comments-content { padding: 15px; height: 120px; }
        .totals-section { flex: 1; border: 1px solid #e5e7eb; padding: 15px; }
        .total-row { display: flex; justify-content: space-between; align-items: center; padding: 8px 0; font-weight: bold; color: #333333; font-size: 14px; }
        .total-final { padding-top: 15px; border-top: 1px solid #e5e7eb; text-align: center; }
        .contact-section { text-align: center; padding: 20px; border-top: 2px solid #333333; margin-top: 20px; font-size: 14px; color: #333333; }
    </style>
</head>
<body>
    <div class="purchase-order-container">
        <div class="container">
            <div class="form-content">
                <div class="header-section">
                    <!-- Section 1: Company Info -->
                    <div class="company-info-section">
                        <div class="section-title">Section 1</div>
                        <div class="company-fields-container">
                            ${companyFieldValues.map(field => `
                                <div class="field-item">
                                    <div class="field-content">
                                        <span class="field-label">${field.label}</span>
                                        <span class="editable-field">${field.value || field.placeholder}</span>
                                    </div>
                                </div>
                            `).join('')}
                        </div>
                    </div>

                    <!-- Section 2: Purchase Order -->
                    <div class="purchase-order-section">
                        <div class="section-title">Section 2</div>
                        <div class="po-fields-container">
                            ${purchaseOrderFieldValues.map(field => `
                                <div class="po-field-item">
                                    <div class="po-field-content">
                                        <span class="${field.isTitle ? 'title-field' : 'po-field-label'}">${field.label}</span>
                                        ${!field.isTitle ? `<span class="editable-field">${field.value || field.placeholder}</span>` : ''}
                                    </div>
                                </div>
                            `).join('')}
                        </div>
                    </div>
                </div>

                <!-- Vendor and Ship To Section -->
                <div class="vendor-shipping-section">
                    <div class="vendor-section">
                        <div class="section-header">Vendor</div>
                        <div class="section-content">
                            ${vendorFields.map(field => `
                                <div class="field-row">
                                    <strong>${field.label}</strong> ${field.placeholder}
                                </div>
                            `).join('')}
                        </div>
                    </div>

                    <div class="ship-to-section">
                        <div class="section-header">Ship To</div>
                        <div class="section-content">
                            ${shipToFields.map(field => `
                                <div class="field-row">
                                    <strong>${field.label}</strong> ${field.placeholder}
                                </div>
                            `).join('')}
                        </div>
                    </div>
                </div>

                <!-- Shipping Details -->
                <div class="shipping-details">
                    <div class="shipping-field"><span class="editable-field">Requisitioner name</span></div>
                    <div class="shipping-field"><span class="editable-field">Shipping method</span></div>
                    <div class="shipping-field"><span class="editable-field">FOB terms</span></div>
                    <div class="shipping-field"><span class="editable-field">Shipping terms</span></div>
                </div>

                <!-- Items Table -->
                <table class="itemtable">
                    <thead>
                        <tr>
                            <th></th>
                            <th colspan="3">Item#</th>
                            <th colspan="12">Description</th>
                            <th colspan="2">Qty</th>
                            <th colspan="3">Rate</th>
                            <th colspan="3">Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        ${[1, 2, 3, 4, 5].map(row => `
                            <tr>
                                <td></td>
                                <td colspan="3"><span class="editable-field">Item number</span></td>
                                <td colspan="12"><span class="editable-field">Item description</span></td>
                                <td colspan="2"><span class="editable-field">Qty</span></td>
                                <td colspan="3"><span class="editable-field">$0.00</span></td>
                                <td colspan="3"><span class="editable-field">$0.00</span></td>
                            </tr>
                        `).join('')}
                    </tbody>
                </table>

                <!-- Comments and Totals -->
                <div class="comments-totals-section">
                    <div class="comments-section">
                        <div class="section-header">Comments or Special Instructions</div>
                        <div class="comments-content">
                            <span class="editable-field">Enter comments or special instructions...</span>
                        </div>
                    </div>

                    <div class="totals-section">
                        <div class="total-row">
                            <span>SUBTOTAL:</span>
                            <span>$0.00</span>
                        </div>
                        <div class="total-row">
                            <span>TAX:</span>
                            <span class="editable-field">$0.00</span>
                        </div>
                        <div class="total-row">
                            <span>SHIPPING:</span>
                            <span class="editable-field">$0.00</span>
                        </div>
                        <div class="total-row">
                            <span>OTHER:</span>
                            <span class="editable-field">$0.00</span>
                        </div>
                        <div class="total-row total-final">
                            <span>TOTAL:</span>
                            <span>$0.00</span>
                        </div>
                    </div>
                </div>

                <!-- Contact Information -->
                <div class="contact-section">
                    <strong>Contact Information:</strong><br>
                    <span class="editable-field">Enter contact information here</span>
                </div>
            </div>
        </div>
    </div>
</body>
</html>`;

    // Create download link
    const blob = new Blob([htmlContent], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `purchase-order-${new Date().toISOString().split('T')[0]}.html`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    console.log('ðŸ“„ HTML exported successfully!');
    console.log('ðŸ“Š Export Summary:', {
      companyFields: companyFieldValues.length,
      purchaseOrderFields: purchaseOrderFieldValues.length,
      totalFields: companyFieldValues.length + purchaseOrderFieldValues.length,
      exportDate: new Date().toLocaleString()
    });
  };

  return (
    <div className="purchase-order-container">
      {/* Page Header */}
      <header className="page-header">
        <h1>Purchase Order Management System</h1>
        <p>Create, edit, and manage purchase orders with ease</p>
      </header>

      {/* Purchase Order Form */}
      <div className="container">
        <div className="form-content">
          {/* Header Section */}
          <div className="section-container">
            <div className="header-section">
              {/* Section-level Drag and Drop */}
              <DndContext 
                sensors={sensors}
                collisionDetection={closestCenter}
                onDragEnd={handleSectionDragEnd}
              >
                <SortableContext 
                  items={sectionOrder}
                  strategy={verticalListSortingStrategy}
                >
                  {/* Section 1: Company Info */}
                  <div id="section1" className="sortable-section">
                    <Section1CompanyInfo 
                      companyFields={companyFields}
                      sensors={sensors}
                      onCompanyDragEnd={handleCompanyDragEnd}
                      onAddCompanyField={handleAddCompanyField}
                      onRemoveCompanyField={handleRemoveCompanyField}
                      onLabelChange={handleCompanyFieldLabelChange}
                      lastModified={lastModified}
                    />
                  </div>

                  {/* Section 2: Purchase Order */}
                  <div id="section2" className="sortable-section">
                    <Section2PurchaseOrder 
                      purchaseOrderFields={purchaseOrderFields}
                      sensors={sensors}
                      onPurchaseOrderDragEnd={handlePurchaseOrderDragEnd}
                      onAddPurchaseOrderField={handleAddPurchaseOrderField}
                      onRemovePurchaseOrderField={handleRemovePurchaseOrderField}
                      onLabelChange={handlePurchaseOrderFieldLabelChange}
                      lastModified={poLastModified}
                    />
                  </div>
                </SortableContext>
              </DndContext>
            </div>
          </div>

          {/* Section 3: Vendor and Ship To */}
          <Section3VendorShipTo 
            vendorFields={vendorFields}
            shipToFields={shipToFields}
            sensors={sensors}
            onVendorDragEnd={handleVendorDragEnd}
            onShipToDragEnd={handleShipToDragEnd}
          />

          {/* Shipping Details */}
          <div className="shipping-details">
            <div className="section-header">REQUISITIONER</div>
            <div className="section-header">SHIP VIA</div>
            <div className="section-header">F.O.B.</div>
            <div className="section-header">SHIPPING TERMS</div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Requisitioner name" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping method" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="FOB terms" /></div>
            <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping terms" /></div>
          </div>

          {/* Items Table */}
          <table className="itemtable">
            <thead>
              <tr>
                <th></th>
                <th colSpan="3">Item#</th>
                <th colSpan="12">Description</th>
                <th colSpan="2">Qty</th>
                <th colSpan="3">Rate</th>
                <th colSpan="3">Amount</th>
              </tr>
            </thead>
            <tbody>
              {[1, 2, 3, 4, 5].map((row) => (
                <tr key={row} className="draggable-row">
                  <td></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="Item number" /></td>
                  <td colSpan="12"><span className="editable-field" contentEditable="true" data-placeholder="Item description" /></td>
                  <td colSpan="2"><span className="editable-field" contentEditable="true" data-placeholder="Qty" /></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="$0.00" /></td>
                  <td colSpan="3"><span className="editable-field" contentEditable="true" data-placeholder="$0.00" /></td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Comments and Totals */}
          <div className="comments-totals-section">
            <div className="comments-section">
              <div className="section-header">Comments or Special Instructions</div>
              <div className="comments-content">
                <span className="editable-field" contentEditable="true" data-placeholder="Enter comments or special instructions..." />
              </div>
            </div>

            <div className="totals-section">
              <div className="total-row">
                <span>SUBTOTAL:</span>
                <span className="calculated-field">$0.00</span>
              </div>
              <div className="total-row">
                <span>TAX:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row">
                <span>SHIPPING:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row">
                <span>OTHER:</span>
                <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
              </div>
              <div className="total-row total-final">
                <span>TOTAL:</span>
                <span className="calculated-field total-field">$0.00</span>
              </div>
            </div>
          </div>

          {/* Contact Information */}
          <div className="contact-section">
            <strong>Contact Information:</strong><br />
            <span className="editable-field" contentEditable="true" data-placeholder="Enter contact information here" />
          </div>




          {/* Convert Button */}
          <div className="button-section">
            <button className="btn">Convert to NetSuite XML</button>
          </div>
        </div>
      </div>

      {/* HTML Mirror Section */}
      <div className="html-mirror-section">
        <div className="mirror-header">
          <h2>ðŸ“„ HTML Mirror - Live Preview</h2>
          <p>This shows how your form will appear when exported to HTML</p>
        </div>

        <div className="mirror-container">
          <div 
            className="mirror-content"
            dangerouslySetInnerHTML={{ __html: generateHTMLMirror() }}
          />
        </div>
      </div>
    </div>
  );
}



export default PurchaseOrderForm;
