  useEffect(() => {
    console.log('🔄 Company fields state updated:', companyFields.map(f => ({ id: f.id, label: f.label })));
  }, [companyFields]);

  // Track last modification time for company fields
  const [lastModified, setLastModified] = useState(new Date().toISOString());

