import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

function SortableShipToField({ field }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} className="ship-to-field-item" data-field={field.id}>
      <div className="drag-handle" {...attributes} {...listeners}>
        ::
      </div>
      <div className="ship-to-field-content">
        <span className="ship-to-field-label">{field.label}</span>
        <span 
          id={field.id} 
          className="editable-field" 
          contentEditable="true" 
          data-placeholder={field.placeholder}
          dangerouslySetInnerHTML={{ __html: field.value || field.placeholder }}
        />
      </div>
    </div>
  );
}

export default SortableShipToField;
