import React, { useState, useEffect, useRef } from 'react';
import './XMLGeneratorModal.css';

const XMLGeneratorModal = ({ isOpen, onClose, formData, onRefresh }) => {
  const [xmlContent, setXmlContent] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState(null);
  const modalRef = useRef(null);
  const xmlOutputRef = useRef(null);

  // Initialize XML generator when component mounts
  useEffect(() => {
    if (isOpen && !window.XML_GENERATOR) {
      initializeXMLGenerator();
    }
  }, [isOpen]);

  // Initialize the XML generator module
  const initializeXMLGenerator = () => {
    // Create a global UTILS object for field mapping
    window.UTILS = {
      getFieldValues: () => {
        return {
          companyName: formData.companyName || 'Your Company Name',
          companyAddress: formData.companyAddress || 'Your Company Address',
          companyCityState: formData.companyCityState || 'City, State ZIP',
          companyPhone: formData.companyPhone || 'Your Company Phone',
          companyFax: formData.companyFax || 'Your Company Fax',
          companyWebsite: formData.companyWebsite || 'Your Company Website',
          poNumber: formData.poNumber || 'PO-001',
          poDate: formData.poDate || new Date().toLocaleDateString(),
          vendorCompany: formData.vendorCompany || 'Vendor Company',
          vendorContact: formData.vendorContact || 'Vendor Contact',
          vendorAddress: formData.vendorAddress || 'Vendor Address',
          vendorCityState: formData.vendorCityState || 'City, ST ZIP',
          vendorPhone: formData.vendorPhone || 'Vendor Phone',
          shipToName: formData.shipToName || 'Ship To Name',
          shipToCompany: formData.shipToCompany || 'Ship To Company',
          shipToAddress: formData.shipToAddress || 'Ship To Address',
          shipToCityState: formData.shipToCityState || 'City, ST ZIP',
          shipToPhone: formData.shipToPhone || 'Ship To Phone',
          shipToFax: formData.shipToFax || '',
          requisitioner: formData.requisitioner || 'Requisitioner Name',
          shipVia: formData.shipVia || 'Standard Shipping',
          fob: formData.fob || 'FOB Origin',
          shippingTerms: formData.shippingTerms || 'Net 30',
          comments: formData.comments || 'No special instructions',
          subtotal: formData.subtotal || '$0.00',
          tax: formData.tax || '$0.00',
          shipping: formData.shipping || '$0.00',
          other: formData.other || '$0.00',
          total: formData.total || '$0.00',
          contactInfo: formData.contactInfo || 'Contact information here'
        };
      },

      getDynamicFieldMapping: () => {
        return {
          companyName: '[data-field-id="company-name"]',
          companyAddress: '[data-field-id="company-address"]',
          companyCityState: '[data-field-id="company-city-state"]',
          companyPhone: '[data-field-id="company-phone"]',
          companyFax: '[data-field-id="company-fax"]',
          companyWebsite: '[data-field-id="company-website"]',
          poNumber: '[data-field-id="po-number"]',
          poDate: '[data-field-id="po-date"]',
          vendorCompany: '[data-field-id="vendor-company"]',
          vendorContact: '[data-field-id="vendor-contact"]',
          vendorAddress: '[data-field-id="vendor-address"]',
          vendorCityState: '[data-field-id="vendor-city-state"]',
          vendorPhone: '[data-field-id="vendor-phone"]',
          shipToName: '[data-field-id="ship-to-name"]',
          shipToCompany: '[data-field-id="ship-to-company"]',
          shipToAddress: '[data-field-id="ship-to-address"]',
          shipToCityState: '[data-field-id="ship-to-city-state"]',
          shipToPhone: '[data-field-id="ship-to-phone"]'
        };
      },

      findFieldByLabel: (label, section) => {
        // Find fields by label and section
        const elements = document.querySelectorAll('.editable-field');
        for (let element of elements) {
          const parentSection = element.closest('.section-header')?.textContent || '';
          if (parentSection.toLowerCase().includes(section.toLowerCase()) && 
              element.textContent.toLowerCase().includes(label.toLowerCase())) {
            return element;
          }
        }
        return null;
      }
    };

    // Create global functions for the modal
    window.MAIN_APP = {
      showError: (message) => {
        setError(message);
        setTimeout(() => setError(null), 5000);
      },
      showSuccess: (message) => {
        // You can implement a success notification here
        console.log('Success:', message);
      }
    };

    // Create global currency formatter
    window.CURRENCY_FORMATTER = {
      format: (value) => {
        if (!value || value.toString().trim() === '') return '$0.00';
        const cleanValue = value.toString().replace(/[$,\s]/g, '');
        if (isNaN(cleanValue) || cleanValue === '') return '$0.00';
        const number = parseFloat(cleanValue);
        return '$' + number.toLocaleString('en-US', { 
          minimumFractionDigits: 2, 
          maximumFractionDigits: 2 
        });
      }
    };

    // Create global drag and drop module
    window.DRAG_AND_DROP = {
      getCurrentCommentsOrder: () => {
        // Return the current order of comments and totals sections
        return { leftSection: 'comments', rightSection: 'totals' };
      }
    };

    // Load the XML generator module
    loadXMLGeneratorModule();
  };

  // Load the XML generator module
  const loadXMLGeneratorModule = () => {
    const script = document.createElement('script');
    script.textContent = `
      // XML Generator Module for NetSuite Integration
      window.XML_GENERATOR = {
        // Convert form data to XML and display in modal
        convertToXml: function() {
          console.log('üîÑ Converting form to XML...');

          try {
            const xmlContent = this.generateXml();
            this.displayXmlModal(xmlContent);
            console.log('‚úÖ XML conversion completed');
          } catch (error) {
            console.error('‚ùå XML conversion failed:', error);
            if (window.MAIN_APP && window.MAIN_APP.showError) {
              window.MAIN_APP.showError('Failed to generate XML. Please check the console for details.');
            }
          }
        },

        // Close the XML modal
        closeXmlModal: function() {
          if (window.MAIN_APP && window.MAIN_APP.closeModal) {
            window.MAIN_APP.closeModal();
          }
        },

        // Refresh XML display (called when rows are reordered)
        refreshXmlDisplay: function() {
          console.log('üîÑ Refreshing XML display...');
          try {
            const xmlContent = this.generateXml();
            if (window.MAIN_APP && window.MAIN_APP.updateXmlContent) {
              window.MAIN_APP.updateXmlContent(xmlContent);
            }
          } catch (error) {
            console.error('‚ùå Failed to refresh XML display:', error);
          }
        },

        // Update XML preview in real-time
        updateXmlPreview: function() {
          console.log('üîÑ Updating XML preview...');
          this.refreshXmlDisplay();
        },

        // Display XML content in modal
        displayXmlModal: function(xmlContent) {
          console.log('üîÑ displayXmlModal called');
          if (window.MAIN_APP && window.MAIN_APP.updateXmlContent) {
            window.MAIN_APP.updateXmlContent(xmlContent);
          }
        },

        // Generate XML from current form data
        generateXml: function() {
          console.log('üöÄ Starting XML generation...');

          // Get field values from UTILS
          let fieldValues;
          if (window.UTILS && window.UTILS.getFieldValues) {
            fieldValues = window.UTILS.getFieldValues();
          } else {
            fieldValues = this.getFieldValues();
          }

          // Generate line items XML (simplified for React integration)
          const lineItems = this.generateLineItemsFromFormData();
          const lineItemsXml = this.generateLineItemsXml(lineItems);

          // Generate the complete XML
          const headerBlockColor = this.getHeaderBlockColorHex();

          const xmlContent = \`<?xml version="1.0"?><!DOCTYPE pdf PUBLIC "-//big.faceless.org//report" "report-1.1.dtd">
<pdf>
<head>
    <meta name="title" value="Purchase Order"/>
    <meta name="author" value="Purchase Order Generator"/>
    <meta name="subject" value="Purchase Order"/>
    <meta name="creator" value="Purchase Order Generator"/>
    <meta name="producer" value="Purchase Order Generator"/>
    <meta name="creationDate" value="\${new Date().toISOString()}"/>
    <meta name="modDate" value="\${new Date().toISOString()}"/>
    <style>
        * { font-family: Arial, Helvetica, sans-serif; font-size: 9pt; }
        table { width: 100%; border-collapse: collapse; }
        .header-company { font-size: 14pt; font-weight: bold; }
        .header-title { font-size: 20pt; font-weight: bold; background-color: \${headerBlockColor}; color: #ffffff; padding: 6px; border: 1px solid #000; }
        .header-info { font-size: 10pt; }
        .section-header { background-color: \${headerBlockColor}; color: #ffffff; font-weight: bold; padding: 6px; border: 1px solid #000; }
        .section-content { padding: 6px; border: 1px solid #000; vertical-align: top; }
        .item-header { background-color: \${headerBlockColor}; color: #ffffff; font-weight: bold; padding: 8px; border: 1px solid #000; }
        .item-cell { padding: 6px; border: 1px solid #000; }
        .total-label { font-weight: bold; padding: 4px; }
        .total-amount { font-weight: bold; padding: 4px; background-color: #ffff99; }
        .comments-header { background-color: \${headerBlockColor}; color: #ffffff; font-weight: bold; padding: 6px; border: 1px solid #000; }
        .comments-content { padding: 6px; border: 1px solid #000; min-height: 40px; }
        .contact-info { font-size: 8pt; }
    </style>
</head>
<body padding="0.5in" size="Letter">
    \${this.generateHeaderXml(fieldValues)}
    \${this.generateVendorXml(fieldValues)}
    \${this.generateShippingXml(fieldValues)}
    \${lineItemsXml}
    \${this.generateCommentsXml(fieldValues)}
    \${this.generateContactInfoXml(fieldValues)}
</body>
</pdf>\`;

          console.log('‚úÖ XML generation completed');
          return xmlContent;
        },

        // Generate header XML
        generateHeaderXml: function(fieldValues) {
          return \`
    <table>
        <tr>
            <td style="width: 65%; padding-right: 20px;">
                <table>
                    <tr>
                        <td class="header-company">\${this.escapeXml(fieldValues.companyName || '')}</td>
                    </tr>
                    <tr>
                        <td>\${this.escapeXml(fieldValues.companyAddress || '')}</td>
                    </tr>
                    <tr>
                        <td>\${this.escapeXml(fieldValues.companyCityState || '')}</td>
                    </tr>
                    <tr>
                        <td>Phone: \${this.escapeXml(fieldValues.companyPhone || '')}</td>
                    </tr>
                    <tr>
                        <td>Fax: \${this.escapeXml(fieldValues.companyFax || '')}</td>
                    </tr>
                    <tr>
                        <td>Website: \${this.escapeXml(fieldValues.companyWebsite || '')}</td>
                    </tr>
                </table>
            </td>
            <td style="width: 35%; padding-left: 20px;" align="right">
                <table>
                    <tr>
                        <td class="header-title">PURCHASE ORDER</td>
                    </tr>
                    <tr>
                        <td>
                            <table style="width: 100%;">
                                <tr>
                                    <td class="header-info" style="width: 30%; text-align: left;"><b>DATE</b></td>
                                    <td class="header-info" style="width: 70%; text-align: left;">\${this.escapeXml(fieldValues.poDate || '')}</td>
                                </tr>
                                <tr>
                                    <td class="header-info" style="width: 30%; text-align: left;"><b>PO #</b></td>
                                    <td class="header-info" style="width: 70%; text-align: left;">\${this.escapeXml(fieldValues.poNumber || '')}</td>
                                </tr>
                            </table>
                        </td>
                    </tr>
                </table>
            </td>
        </tr>
    </table>\`;
        },

        // Generate vendor XML
        generateVendorXml: function(fieldValues) {
          return \`
    <table style="margin-top: 20px;">
        <tr>
            <td style="width: 50%;">
                <table>
                    <tr>
                        <td class="section-header">VENDOR</td>
                    </tr>
                    <tr>
                        <td class="section-content">
                            \${this.escapeXml(fieldValues.vendorCompany)}<br/>
                            \${this.escapeXml(fieldValues.vendorContact || '')}<br/>
                            \${this.escapeXml(fieldValues.vendorAddress)}<br/>
                            \${this.escapeXml(fieldValues.vendorCityState)}<br/>
                            Phone: \${this.escapeXml(fieldValues.vendorPhone)}<br/>
                            Fax: \${this.escapeXml(fieldValues.vendorFax || '')}
                        </td>
                    </tr>
                </table>
            </td>
            <td style="width: 50%;">
                <table>
                    <tr>
                        <td class="section-header">SHIP TO</td>
                    </tr>
                    <tr>
                        <td class="section-content">
                            \${this.escapeXml(fieldValues.shipToName || '')}<br/>
                            \${this.escapeXml(fieldValues.shipToCompany || fieldValues.companyName)}<br/>
                            \${this.escapeXml(fieldValues.shipToAddress || fieldValues.companyAddress)}<br/>
                            \${this.escapeXml(fieldValues.shipToCityState || fieldValues.companyCityState)}<br/>
                            Phone: \${this.escapeXml(fieldValues.shipToPhone || fieldValues.companyPhone || '')}<br/>
                            Fax: \${this.escapeXml(fieldValues.shipToFax || '')}
                        </td>
                    </tr>
                </table>
            </td>
        </tr>
    </table>\`;
        },

        // Generate shipping XML
        generateShippingXml: function(fieldValues) {
          return \`
    <table style="margin-top: 15px;">
        <tr>
            <td class="section-header" style="width: 25%;">REQUISITIONER</td>
            <td class="section-header" style="width: 25%;">SHIP VIA</td>
            <td class="section-header" style="width: 25%;">F.O.B.</td>
            <td class="section-header" style="width: 25%;">SHIPPING TERMS</td>
        </tr>
        <tr>
            <td class="section-content">\${this.escapeXml(fieldValues.requisitioner || '')}</td>
            <td class="section-content">\${this.escapeXml(fieldValues.shipVia || '')}</td>
            <td class="section-content">\${this.escapeXml(fieldValues.fob || '')}</td>
            <td class="section-content">\${this.escapeXml(fieldValues.shippingTerms || '')}</td>
        </tr>
    </table>\`;
        },

        // Generate line items from form data
        generateLineItemsFromFormData: function() {
          // This would be populated from your form's line items
          // For now, return a sample item
          return [
            {
              item: 'Sample Item',
              description: 'Sample Description',
              quantity: '1',
              rate: '10.00',
              amount: '10.00'
            }
          ];
        },

        // Generate line items XML
        generateLineItemsXml: function(lineItems) {
          if (!lineItems || lineItems.length === 0) {
            return '';
          }

          let tableHeaders = \`
        <tr>
            <td class="item-header" align="center" colspan="3">ITEM#</td>
            <td class="item-header" align="center" colspan="12">DESCRIPTION</td>
            <td class="item-header" align="center" colspan="2">QTY</td>
            <td class="item-header" align="center" colspan="3">RATE</td>
            <td class="item-header" align="center" colspan="3">AMOUNT</td>
        </tr>\`;

          let tableRows = '';
          lineItems.forEach((item, index) => {
            tableRows += \`\n        <tr>\`;
            tableRows += \`\n            <td class="item-cell" align="center" colspan="3">\${this.escapeXml(item.item || '')}</td>\`;
            tableRows += \`\n            <td class="item-cell" align="left" colspan="12">\${this.escapeXml(item.description || '')}</td>\`;
            tableRows += \`\n            <td class="item-cell" align="center" colspan="2">\${this.escapeXml(item.quantity || '')}</td>\`;
            tableRows += \`\n            <td class="item-cell" align="right" colspan="3">\${this.escapeXml(item.rate || '')}</td>\`;
            tableRows += \`\n            <td class="item-cell" align="right" colspan="3">\${this.escapeXml(item.amount || '')}</td>\`;
            tableRows += \`\n        </tr>\`;
          });

          return \`
    <table style="margin-top: 15px;">
        \${tableHeaders}\${tableRows}
    </table>\`;
        },

        // Generate comments XML
        generateCommentsXml: function(fieldValues) {
          return \`
    <table style="margin-top: 15px;">
        <tr>
            <td style="width: 70%; vertical-align: top;">
                <table style="width: 100%; border: 1px solid #e5e7eb;">
                    <tr>
                        <td class="comments-header">Comments or Special Instructions</td>
                    </tr>
                    <tr>
                        <td class="comments-content" style="padding: 15px; height: 120px; vertical-align: top;">\${this.escapeXml(fieldValues.comments || '')}</td>
                    </tr>
                </table>
            </td>
            <td style="width: 30%; padding: 0;">
                <table>
                    <tr>
                        <td class="total-label" align="right">SUBTOTAL</td>
                        <td class="total-label" align="right">\${this.escapeXml(this.formatCurrency(fieldValues.subtotal || ''))}</td>
                    </tr>
                    <tr>
                        <td class="total-label" align="right">TAX</td>
                        <td class="total-label" align="right">\${this.escapeXml(this.formatCurrency(fieldValues.tax || ''))}</td>
                    </tr>
                    <tr>
                        <td class="total-label" align="right">SHIPPING</td>
                        <td class="total-label" align="right">\${this.escapeXml(this.formatCurrency(fieldValues.shipping || ''))}</td>
                    </tr>
                    <tr>
                        <td class="total-label" align="right">OTHER</td>
                        <td class="total-label" align="right">\${this.escapeXml(this.formatCurrency(fieldValues.other || ''))}</td>
                    </tr>
                    <tr>
                        <td class="total-label" align="right">TOTAL</td>
                        <td class="total-amount" align="right">\${this.escapeXml(this.formatCurrency(fieldValues.total || ''))}</td>
                    </tr>
                </table>
            </td>
        </tr>
    </table>\`;
        },

        // Generate contact info XML
        generateContactInfoXml: function(fieldValues) {
          return \`
    <table style="margin-top: 20px;">
        <tr>
            <td class="contact-info" style="width: 70%;">
                \${this.formatContactInfo(fieldValues.contactInfo, fieldValues.companyName, fieldValues.companyPhone)}
            </td>
            <td style="width: 30%; text-align: center;">
                <table style="width: 100%;">
                    <tr>
                        <td style="border-top: 1px solid #000; padding-top: 10px;">
                            Authorized Signature
                        </td>
                    </tr>
                </table>
            </td>
        </tr>
    </table>\`;
        },

        // Get header block color
        getHeaderBlockColorHex: function() {
          return '#333333';
        },

        // Format currency
        formatCurrency: function(value) {
          return window.CURRENCY_FORMATTER ? window.CURRENCY_FORMATTER.format(value) : '\$0.00';
        },

        // Format contact info
        formatContactInfo: function(contactInfo, companyName, companyPhone) {
          if (!contactInfo) {
            const fallback = \`For questions about this purchase order, please contact \${companyName || 'us'}\`;
            return this.escapeXml(companyPhone ? \`\${fallback} at \${companyPhone}\` : fallback);
          }
          return this.escapeXml(contactInfo.toString().trim());
        },

        // Escape XML
        escapeXml: function(text) {
          if (!text) return '';
          return text.replace(/&/g, '&amp;')
                    .replace(/</g, '&lt;')
                    .replace(/>/g, '&gt;')
                    .replace(/"/g, '&quot;')
                    .replace(/'/g, '&apos;');
        },

        // Get field values (fallback)
        getFieldValues: function() {
          return window.UTILS ? window.UTILS.getFieldValues() : {};
        }
      };

      // Global functions for the modal
      window.closeXmlModal = function() {
        if (window.MAIN_APP && window.MAIN_APP.closeModal) {
          window.MAIN_APP.closeModal();
        }
      };

      window.copyXmlToClipboard = function() {
        const xmlText = window.MAIN_APP ? window.MAIN_APP.getXmlContent() : '';
        if (navigator.clipboard && navigator.clipboard.writeText) {
          navigator.clipboard.writeText(xmlText).then(() => {
            if (window.MAIN_APP && window.MAIN_APP.showSuccess) {
              window.MAIN_APP.showSuccess('XML copied to clipboard!');
            }
          }).catch(err => {
            console.error('Failed to copy XML to clipboard:', err);
          });
        }
      };

      window.downloadXml = function() {
        const xmlText = window.MAIN_APP ? window.MAIN_APP.getXmlContent() : '';
        const blob = new Blob([xmlText], { type: 'application/xml' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'purchase-order.xml';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);

        if (window.MAIN_APP && window.MAIN_APP.showSuccess) {
          window.MAIN_APP.showSuccess('XML file downloaded!');
        }
      };

      console.log('‚úÖ XML Generator module loaded successfully');
    `;

    document.head.appendChild(script);
  };

  // Generate XML when modal opens
  useEffect(() => {
    if (isOpen && window.XML_GENERATOR) {
      generateXML();
    }
  }, [isOpen, formData]);

  // Generate XML content
  const generateXML = async () => {
    setIsGenerating(true);
    setError(null);

    try {
      if (window.XML_GENERATOR) {
        const xml = window.XML_GENERATOR.generateXml();
        setXmlContent(xml);

        // Update MAIN_APP functions
        window.MAIN_APP.updateXmlContent = (content) => setXmlContent(content);
        window.MAIN_APP.closeModal = onClose;
        window.MAIN_APP.getXmlContent = () => xmlContent;
      }
    } catch (err) {
      setError('Failed to generate XML: ' + err.message);
      console.error('XML generation error:', err);
    } finally {
      setIsGenerating(false);
    }
  };

  // Handle refresh
  const handleRefresh = () => {
    if (onRefresh) {
      onRefresh();
    }
    generateXML();
  };

  // Handle copy to clipboard
  const handleCopyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(xmlContent);
      // You can add a success notification here
    } catch (err) {
      console.error('Failed to copy to clipboard:', err);
    }
  };

  // Handle download
  const handleDownload = () => {
    const blob = new Blob([xmlContent], { type: 'application/xml' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'purchase-order.xml';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (!isOpen) return null;

  return (
    <div className="xml-modal-overlay" onClick={onClose}>
      <div className="xml-modal" ref={modalRef} onClick={(e) => e.stopPropagation()}>
        <div className="xml-modal-header">
          <h2>üìÑ XML Generator</h2>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>

        <div className="xml-modal-content">
          {error && (
            <div className="error-message">
              ‚ùå {error}
            </div>
          )}

          <div className="xml-controls">
            <button 
              className="btn refresh-btn" 
              onClick={handleRefresh}
              disabled={isGenerating}
            >
              üîÑ Refresh XML
            </button>
            <button 
              className="btn copy-btn" 
              onClick={handleCopyToClipboard}
              disabled={!xmlContent}
            >
              üìã Copy to Clipboard
            </button>
            <button 
              className="btn download-btn" 
              onClick={handleDownload}
              disabled={!xmlContent}
            >
              üíæ Download XML
            </button>
          </div>

          <div className="xml-output-container">
            <div className="xml-output-header">
              <h3>Generated XML</h3>
              <span className="xml-info">
                {xmlContent ? `${xmlContent.length} characters` : 'No XML generated'}
              </span>
            </div>

            <div className="xml-output" ref={xmlOutputRef}>
              {isGenerating ? (
                <div className="loading">üîÑ Generating XML...</div>
              ) : xmlContent ? (
                <pre className="xml-content">{xmlContent}</pre>
              ) : (
                <div className="no-content">No XML content available</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default XMLGeneratorModal;
