      const oldLabel = oldField ? oldField.label : '';

      logChange('label-change', fieldId, oldLabel, newLabel, 'company');

      return prevFields.map(field => 
        field.id === fieldId ? { ...field, label: newLabel } : field
      );
    });
    updateCompanyFieldsTimestamp();
