// Purchase Order XML Template for NetSuite Integration
// This template generates XML compatible with Big Faceless Report Generator (BFR)
// and dynamically reflects the current section order from the DOM

// Function to generate XML from React form data
export function generatePurchaseOrderXML(formData) {
  // Ensure formData exists and has required properties
  const data = formData || {};

  // Helper function to safely get field values with fallbacks
  const getFieldValue = (fieldName, defaultValue = '') => {
    return data[fieldName] || defaultValue;
  };

  // Helper function to get company field values
  const getCompanyFieldValue = (fieldId, defaultValue = '') => {
    if (data.companyFields && Array.isArray(data.companyFields)) {
      const field = data.companyFields.find(f => f.id === fieldId);
      return field ? (field.value || defaultValue) : defaultValue;
    }
    return defaultValue;
  };

  // Helper function to get purchase order field values
  const getPOFieldValue = (fieldId, defaultValue = '') => {
    if (data.purchaseOrderFields && Array.isArray(data.purchaseOrderFields)) {
      const field = data.purchaseOrderFields.find(f => f.id === fieldId);
      return field ? (field.value || defaultValue) : defaultValue;
    }
    return defaultValue;
  };

  // Helper function to get vendor field values
  const getVendorFieldValue = (fieldId, defaultValue = '') => {
    if (data.vendorFields && Array.isArray(data.vendorFields)) {
      const field = data.vendorFields.find(f => f.id === fieldId);
      return field ? (field.value || defaultValue) : defaultValue;
    }
    return defaultValue;
  };

  // Helper function to get ship-to field values
  const getShipToFieldValue = (fieldId, defaultValue = '') => {
    if (data.shipToFields && Array.isArray(data.shipToFields)) {
      const field = data.shipToFields.find(f => f.id === fieldId);
      return field ? (field.value || defaultValue) : defaultValue;
    }
    return defaultValue;
  };

  // Function to detect current section order from DOM
  const getCurrentSectionOrder = () => {
    try {
      // Look for the main container
      const container = document.querySelector('.purchase-order-container') || 
                      document.querySelector('[class*="container"]') ||
                      document.body;

      if (!container) return ['header', 'vendor', 'shipping', 'items', 'comments'];

      const sections = [];
      const children = Array.from(container.children);

      console.log('üîç Detecting section order from DOM...', children.length, 'children found');

      children.forEach((child, index) => {
        if (child.classList.contains('draggable-header-row') || 
            child.querySelector('.header-cell')) {
          sections.push('header');
          console.log(`  ‚úÖ Found header section at index ${index}`);
        } else if (child.classList.contains('draggable-vendor-section') || 
                   child.querySelector('.vendor-cell')) {
          sections.push('vendor');
          console.log(`  ‚úÖ Found vendor section at index ${index}`);
        } else if (child.classList.contains('draggable-shipping-section') || 
                   child.querySelector('.shipping-details')) {
          sections.push('shipping');
          console.log(`  ‚úÖ Found shipping section at index ${index}`);
        } else if (child.classList.contains('itemtable') || 
                   child.querySelector('.itemtable')) {
          sections.push('items');
          console.log(`  ‚úÖ Found items section at index ${index}`);
        } else if (child.querySelector('.comments-section') || 
                   child.querySelector('.totals-section')) {
          sections.push('comments');
          console.log(`  ‚úÖ Found comments section at index ${index}`);
        }
      });

      console.log('üîç Final detected section order:', sections);
      return sections.length > 0 ? sections : ['header', 'vendor', 'shipping', 'items', 'comments'];
    } catch (error) {
      console.warn('‚ö†Ô∏è Error detecting section order, using default:', error);
      return ['header', 'vendor', 'shipping', 'items', 'comments'];
    }
  };

  // Function to detect current header section order (company info vs purchase order)
  const getCurrentHeaderOrder = () => {
    try {
      const headerCells = document.querySelectorAll('.header-cell');
      if (headerCells.length >= 2) {
        const firstCell = headerCells[0];
        const secondCell = headerCells[1];

        // Check which is company info vs purchase order
        const firstIsCompany = firstCell.classList.contains('company-info') || 
                              firstCell.querySelector('[id*="company"]');
        const secondIsCompany = secondCell.classList.contains('company-info') || 
                               secondCell.querySelector('[id*="company"]');

        if (firstIsCompany && !secondIsCompany) {
          return { leftSection: 'company-info', rightSection: 'purchase-order' };
        } else if (!firstIsCompany && secondIsCompany) {
          return { leftSection: 'purchase-order', rightSection: 'company-info' };
        }
      }

      // Default order
      return { leftSection: 'company-info', rightSection: 'purchase-order' };
    } catch (error) {
      console.warn('‚ö†Ô∏è Error detecting header order, using default:', error);
      return { leftSection: 'company-info', rightSection: 'purchase-order' };
    }
  };

  // Function to detect current vendor section order (vendor vs ship-to)
  const getCurrentVendorOrder = () => {
    try {
      const vendorCells = document.querySelectorAll('.vendor-cell');
      if (vendorCells.length >= 2) {
        const firstCell = vendorCells[0];
        const secondCell = vendorCells[1];

        // Check which is vendor vs ship-to
        const firstIsVendor = firstCell.classList.contains('vendor-section') || 
                             firstCell.querySelector('[id*="vendor"]');
        const secondIsVendor = secondCell.classList.contains('vendor-section') || 
                              secondCell.querySelector('[id*="vendor"]');

        if (firstIsVendor && !secondIsVendor) {
          return { leftSection: 'vendor', rightSection: 'ship-to' };
        } else if (!firstIsVendor && secondIsVendor) {
          return { leftSection: 'ship-to', rightSection: 'vendor' };
        }
      }

      // Default order
      return { leftSection: 'vendor', rightSection: 'ship-to' };
    } catch (error) {
      console.warn('‚ö†Ô∏è Error detecting vendor order, using default:', error);
      return { leftSection: 'vendor', rightSection: 'ship-to' };
    }
  };

  // Generate line items XML
  const generateLineItemsXML = () => {
    if (!data.lineItems || !Array.isArray(data.lineItems)) {
      // Return empty rows if no line items
      return Array(5).fill(0).map((_, index) => `
      <tr>
        <td></td>
        <td colspan="3"><span class="editable-field">-</span></td>
        <td colspan="12"><span class="editable-field">-</span></td>
        <td colspan="2"><span class="editable-field">-</span></td>
        <td colspan="3"><span class="editable-field">-</span></td>
        <td colspan="3"><span class="editable-field">-</span></td>
      </tr>
    `).join('');
    }

    return data.lineItems.map((item, index) => `
      <tr>
        <td></td>
        <td colspan="3"><span class="editable-field">${item.itemNumber || ''}</span></td>
        <td colspan="12"><span class="editable-field">${item.description || ''}</span></td>
        <td colspan="2"><span class="editable-field">${item.quantity || ''}</span></td>
        <td colspan="3"><span class="editable-field">${item.unitPrice || ''}</span></td>
        <td colspan="3"><span class="editable-field">${item.total || ''}</span></td>
      </tr>
    `).join('');
  };

  // Generate header section XML based on current order
  const generateHeaderXML = () => {
    const headerOrder = getCurrentHeaderOrder();

    const companyInfo = `
                            <!-- Company Info -->
                            <span>${getCompanyFieldValue('company-name', '[Company Name]')}</span><br />
                            <span>${getCompanyFieldValue('company-address', '[Street Address]')}</span><br />
                            <span>${getCompanyFieldValue('company-city-state', '[City, ST ZIP]')}</span><br />
                            <span>Phone: ${getCompanyFieldValue('company-phone', '(000) 000-0000')}</span><br />
                            <span>Fax: ${getCompanyFieldValue('company-fax', '(000) 000-0000')}</span><br />
                            <span>Website: ${getCompanyFieldValue('company-website', 'www.example.com')}</span>`;

    const purchaseOrder = `
                            <!-- PO Title and Details -->
                            <span class="po-title">PURCHASE ORDER</span><br />
                            <span style="margin-top: 20px;">
                                <span>DATE: ${getPOFieldValue('po-date', 'MM/DD/YYYY')}</span><br />
                                <span>PO #: ${getPOFieldValue('po-number', '[PO Number]')}</span>
                            </span>`;

    if (headerOrder.leftSection === 'company-info') {
      return `
                        <td width="50%">
                            ${companyInfo}
                        </td>
                        <td width="50%" style="text-align: right;">
                            ${purchaseOrder}
                        </td>`;
    } else {
      return `
                        <td width="50%">
                            ${purchaseOrder}
                        </td>
                        <td width="50%" style="text-align: right;">
                            ${companyInfo}
                        </td>`;
    }
  };

  // Generate vendor section XML based on current order
  const generateVendorXML = () => {
    const vendorOrder = getCurrentVendorOrder();

    const vendorInfo = `
                            <!-- Vendor Info -->
                            <table style="width: 100%; border: none;">
                                <tr>
                                    <td class="section-header">VENDOR</td>
                                </tr>
                                <tr>
                                    <td style="padding: 15px; border: none;">
                                        <span>${getVendorFieldValue('vendor-company', '[Company Name]')}</span><br />
                                        <span>${getVendorFieldValue('vendor-contact', '[Contact or Department]')}</span><br />
                                        <span>${getVendorFieldValue('vendor-address', '[Street Address]')}</span><br />
                                        <span>${getVendorFieldValue('vendor-city-state', '[City, ST ZIP]')}</span><br />
                                        <span>Phone: ${getVendorFieldValue('vendor-phone', '(000) 000-0000')}</span><br />
                                        <span>Fax: ${getVendorFieldValue('vendor-fax', '(000) 000-0000')}</span>
                                    </td>
                                </tr>
                            </table>`;

    const shipToInfo = `
                            <!-- Ship To Info -->
                            <table style="width: 100%; border: none;">
                                <tr>
                                    <td class="section-header">SHIP TO</td>
                                </tr>
                                <tr>
                                    <td style="padding: 15px; border: none;">
                                        <span>${getShipToFieldValue('ship-to-name', '[Name]')}</span><br />
                                        <span>${getShipToFieldValue('ship-to-company', '[Company Name]')}</span><br />
                                        <span>${getShipToFieldValue('ship-to-address', '[Street Address]')}</span><br />
                                        <span>${getShipToFieldValue('ship-to-city-state', '[City, ST ZIP]')}</span><br />
                                        <span>Phone: ${getShipToFieldValue('ship-to-phone', '[Phone]')}</span>
                                    </td>
                                </tr>
                            </table>`;

    if (vendorOrder.leftSection === 'vendor') {
      return `
                        <td width="50%" style="vertical-align: top; padding-right: 20px;">
                            ${vendorInfo}
                        </td>
                        <td width="50%" style="vertical-align: top; padding-left: 20px;">
                            ${shipToInfo}
                        </td>`;
    } else {
      return `
                        <td width="50%" style="vertical-align: top; padding-right: 20px;">
                            ${shipToInfo}
                        </td>
                        <td width="50%" style="vertical-align: top; padding-left: 20px;">
                            ${vendorInfo}
                        </td>`;
    }
  };

  // Generate sections in dynamic order
  const generateDynamicSectionsXML = () => {
    const sectionOrder = getCurrentSectionOrder();
    let sectionsXml = '';

    sectionOrder.forEach(sectionType => {
      switch(sectionType) {
        case 'header':
          sectionsXml += `
        <tr>
            <td>
                <!-- Header Table -->
                <table class="header-table">
                    <tr>
                        ${generateHeaderXML()}
                    </tr>
                </table>
            </td>
        </tr>`;
          break;

        case 'vendor':
          sectionsXml += `
        <tr>
            <td>
                <!-- Vendor and Ship To Table -->
                <table style="width: 100%; border: none; margin-top: 20px;">
                    <tr>
                        ${generateVendorXML()}
                    </tr>
                </table>
            </td>
        </tr>`;
          break;

        case 'shipping':
          sectionsXml += `
        <tr>
            <td>
                <!-- Shipping Details Bar -->
                <table style="width: 100%; border: none;">
                    <tr>
                        <td width="25%" style="text-align: center; padding: 10px; border: none;">
                            <span class="section-header">REQUISITIONER</span><br />
                            <span>${getFieldValue('requisitioner', '[Requisitioner]')}</span>
                        </td>
                        <td width="25%" style="text-align: center; padding: 10px; border: none;">
                            <span class="section-header">SHIP VIA</span><br />
                            <span>${getFieldValue('shipVia', '[Ship Via]')}</span>
                        </td>
                        <td width="25%" style="text-align: center; padding: 10px; border: none;">
                            <span class="section-header">F.O.B.</span><br />
                            <span>${getFieldValue('fob', '[FOB]')}</span>
                        </td>
                        <td width="25%" style="text-align: center; padding: 10px; border: none;">
                            <span class="section-header">SHIPPING TERMS</span><br />
                            <span>${getFieldValue('shippingTerms', '[Shipping Terms]')}</span>
                        </td>
                    </tr>
                </table>
            </td>
        </tr>`;
          break;

        case 'items':
          sectionsXml += `
        <tr>
            <td>
                <!-- Line Items Table -->
                <table class="itemtable">
                    <thead>
                        <tr>
                            <th></th>
                            <th colspan="3">Item#</th>
                            <th colspan="12">Description</th>
                            <th colspan="2">Qty</th>
                            <th colspan="3">Rate</th>
                            <th colspan="3">Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        ${generateLineItemsXML()}
                    </tbody>
                </table>
            </td>
        </tr>`;
          break;

        case 'comments':
          sectionsXml += `
        <tr>
            <td>
                <!-- Comments and Totals -->
                <table style="width: 100%;">
                    <tr>
                        <td width="50%" style="vertical-align: top;">
                            <!-- Comments Section -->
                            <span class="comments-section">
                                <span class="section-header">Comments or Special Instructions</span>
                                <span style="padding: 10px; min-height: 80px; display: block; margin-top: 10px;">
                                    <span class="editable-field">${getFieldValue('comments', '')}</span>
                                </span>
                            </span>
                        </td>
                        <td width="50%" style="vertical-align: top; padding-left: 40px;">
                            <!-- Totals Section -->
                            <span class="totals-section">
                                <span class="total-row">
                                    <span>SUBTOTAL:</span>
                                    <span style="margin-left: 20px;">${getFieldValue('subtotal', '$0.00')}</span>
                                </span><br />
                                <span class="total-row">
                                    <span>TAX:</span>
                                    <span style="margin-left: 20px;">${getFieldValue('tax', '$0.00')}</span>
                                </span><br />
                                <span class="total-row">
                                    <span>SHIPPING:</span>
                                    <span style="margin-left: 20px;">${getFieldValue('shipping', '$0.00')}</span>
                                </span><br />
                                <span class="total-row">
                                    <span>OTHER:</span>
                                    <span style="margin-left: 20px;">${getFieldValue('other', '$0.00')}</span>
                                </span><br />
                                <span class="total-row total-final">
                                    <span>TOTAL:</span>
                                    <span style="margin-left: 20px;">${getFieldValue('total', '$0.00')}</span>
                                </span>
                            </span>
                        </td>
                    </tr>
                </table>
            </td>
        </tr>

        <tr>
            <td>
                <!-- Contact Information -->
                <span class="contact-section">
                    <strong>If you have any questions about this purchase order, please contact:</strong><br />
                    <span class="editable-field">${getFieldValue('contactInfo', '[Name, Phone #, E-mail]')}</span>
                </span>
            </td>
        </tr>`;
          break;

        default:
          console.warn(`‚ö†Ô∏è Unknown section type: ${sectionType}`);
      }
    });

    return sectionsXml;
  };

  // Generate the complete XML using dynamic section detection
  const xml = `<?xml version="1.0"?>
<!DOCTYPE pdf PUBLIC "-//big.faceless.org//report" "report-1.1.dtd">
<pdf>
<head>
    <meta name="title" value="Purchase Order"/>
    <meta name="author" value="Purchase Order Generator"/>
    <meta name="subject" value="Purchase Order"/>
    <meta name="creator" value="Purchase Order Generator"/>
    <meta name="producer" value="Purchase Order Generator"/>
    <meta name="creationDate" value="${new Date().toISOString()}"/>
    <meta name="modDate" value="${new Date().toISOString()}"/>
    <style type="text/css">
        * { font-family: Arial, sans-serif; font-size: 9pt; }
        table { 
            width: 100%; 
            border-collapse: collapse; 
            margin-bottom: 10px;
        }
        .main-container { 
            border: none; 
            padding: 20px; 
        }
        .header-table { 
            border: none; 
            margin-bottom: 20px;
        }
        .section-header { 
            background-color: #333; 
            color: #ffffff; 
            font-weight: bold; 
            padding: 8px; 
            text-align: center;
            border: none;
        }
        .po-title { 
            font-size: 20pt; 
            font-weight: bold; 
            color: #333; 
            text-align: center;
            background-color: #f0f0f0;
            padding: 6px;
            border: none;
        }
        .editable-field { 
            min-height: 20px; 
            display: inline-block;
        }
        .shipping-details { 
            margin: 20px 0;
        }
        .shipping-item { 
            text-align: center; 
            padding: 10px;
        }
        .itemtable { 
            border: none; 
            margin: 20px 0;
        }
        .itemtable th { 
            background-color: #333; 
            color: #ffffff;
            padding: 10px; 
            border: none;
            font-weight: bold;
        }
        .itemtable td { 
            padding: 8px; 
            border: none;
        }
        .totals-section { 
            text-align: right; 
            margin: 20px 0;
        }
        .total-row { 
            margin: 5px 0; 
            font-weight: bold;
        }
        .total-final { 
            font-size: 18px; 
            color: #333;
            background-color: #ffff99;
            padding: 4px;
        }
        .comments-section { 
            margin: 20px 0;
        }
        .contact-section { 
            text-align: center; 
            margin: 20px 0; 
            padding: 10px;
        }
        .section-content { 
            padding: 6px; 
            border: none; 
            vertical-align: top;
        }
    </style>
</head>
<body padding="0.5in" size="Letter">
    <table class="main-container">
        ${generateDynamicSectionsXML()}
    </table>
</body>
</pdf>`;

  console.log('‚úÖ XML generated with dynamic section order detection');
  return xml;
}

