  // Function to generate HTML mirror from current React state
  function generateHTMLMirror() {
    return `
      <!-- Purchase Order Form Template -->
      <table class="container">
          <tr>
              <td style="padding: 40px 50px;">
                  <!-- Header Section -->
                  <div class="section-container">
                      <table class="header-section" style="width: 100%; margin-bottom: 40px;">
                          <tr>
                              <td class="header-cell company-info" style="width: 50%; vertical-align: top;">
                                  <table style="width: 100%;">
                                      <tr>
                                          <td style="padding-top: 15px;">
                                              <div class="company-fields-container">
                                                                                                ${companyFields.map(field => `
                                                  <div class="field-item" data-field="${field.id}" data-id="${field.id}">
                                                      <div class="field-content">
                                                          <span class="field-label">${field.label}</span>
                                                          <span id="${field.id}" class="field-value" data-placeholder="${field.placeholder}"></span>
                                                      </div>
                                                  </div>
                                              `).join('')}
                                              </div>
                                          </td>
                                      </tr>
                                  </table>
                              </td>

                              <td class="header-cell purchase-order" style="width: 50%; text-align: center; vertical-align: top;">
                              <table style="width: 100%;">
                                  <tr>
                                      <td style="text-align: center; font-size: 32px; font-weight: bold; color: #000000; padding: 10px 0;">
                                          Purchase Order
                                      </td>
                                  </tr>
                                  <tr>
                                      <td style="padding-top: 15px;">
                                          <div style="text-align: center;">
                                              ${purchaseOrderFields.filter(field => !field.isTitle).map(field => `
                                                  <div style="margin-bottom: 8px;">
                                                      <span style="font-weight: bold; font-size: 12px; margin-right: 8px;">${field.label}</span>
                                                      <span id="${field.id}" class="field-value" data-placeholder="${field.placeholder}"></span>
                                                  </div>
                                              `).join('')}
                                          </div>
                                      </td>
                                  </tr>
                              </table>
                          </td>
                      </tr>
                  </table>
                  </div>

                  <!-- Vendor and Ship To Section -->
                  <div class="vendor-section" style="margin-bottom: 35px;">
                      <table style="width: 100%; table-layout: fixed;">
                          <tr>
                              <td class="vendor-cell vendor-section" style="width: 50%; padding-right: 5px; vertical-align: top;">
                              <table style="width: 100%; border: 1px solid #e5e7eb;">
                                  <tr>
                                      <td class="section-header" style="background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">VENDOR</td>
                                  </tr>
                                  <tr>
                                      <td style="padding: 10px; height: 100px; vertical-align: top;">
                                          <div style="font-size: 11px; line-height: 1.3;">
                                              ${vendorFields.map(field => `
                                                  <div style="margin-bottom: 3px;">${field.label} <span id="${field.id}" class="field-value" data-placeholder="${field.placeholder}" style="font-weight: normal; font-size: 11px;"></span></div>
                                              `).join('')}
                                          </div>
                                      </td>
                                  </tr>
                              </table>
                          </td>

                          <td class="vendor-cell ship-to-section" style="width: 50%; padding-left: 5px; vertical-align: top;" data-section="ship-to">
                              <table style="width: 100%; border: 1px solid #e5e7eb;">
                                  <tr>
                                      <td class="section-header" style="background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">SHIP TO</td>
                                  </tr>
                                  <tr>
                                      <td style="padding: 10px; height: 100px; vertical-align: top;">
                                          <div style="font-size: 11px; line-height: 1.3;">
                                              ${shipToFields.map(field => `
                                                  <div style="margin-bottom: 3px;">${field.label} <span id="${field.id}" class="field-value" data-placeholder="${field.placeholder}" style="font-weight: normal; font-size: 11px;"></span></div>
                                              `).join('')}
                                          </div>
                                      </td>
                                  </tr>
                              </table>
                          </td>
                      </tr>
                      </table>
                  </div>

                  <!-- Shipping Details Row -->
                  <div class="shipping-section" style="margin-bottom: 30px;">
                      <table class="shipping-details" style="width: 100%; border: 1px solid #e5e7eb;">
                          <tr>
                              <td class="section-header" style="width: 25%; background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">REQUISITIONER</td>
                              <td class="section-header" style="width: 25%; background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">SHIP VIA</td>
                              <td class="section-header" style="width: 25%; background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">F.O.B.</td>
                              <td class="section-header" style="width: 25%; background: #333333; color: white; padding: 8px; font-weight: bold; text-align: center; font-size: 12px;">SHIPPING TERMS</td>
                          </tr>
                          <tr>
                              <td style="padding: 10px; height: 50px; border-right: 1px solid #e5e7eb; vertical-align: top;">
                                  <span class="field-value" data-placeholder="Requisitioner name" style="font-size: 12px;"></span>
                              </td>
                              <td style="padding: 10px; height: 50px; border-right: 1px solid #e5e7eb; vertical-align: top;">
