        console.log('ðŸ“Š New section order:', newOrder);

        return newOrder;
      });
    } else {
      console.log('ðŸ” No vendor/ship-to swap - same position');
    }
  };

  // Function to handle vendor-ship-to and shipping-details sections reordering
  const handleVendorShipToAndShippingDetailsDragEnd = (event) => {
    const { active, over } = event;

    console.log('ðŸ” VENDOR-SHIP-TO + SHIPPING DETAILS DRAG DEBUG - Event received:', { active, over });

    if (active.id !== over.id) {
      console.log('ðŸ”„ Vendor-ShipTo + Shipping Details swap detected!', { active: active.id, over: over.id });

      setVendorShipToAndShippingDetailsOrder(prevOrder => {
        console.log('ðŸ”„ Previous vendor-shipTo + shipping details order:', prevOrder);

        const oldIndex = prevOrder.indexOf(active.id);
        const newIndex = prevOrder.indexOf(over.id);

        console.log('ðŸ”„ Index calculations:', { oldIndex, newIndex });

        const newOrder = [...prevOrder];
        newOrder.splice(oldIndex, 1);
        newOrder.splice(newIndex, 0, active.id);

        console.log('ðŸ”„ New vendor-shipTo + shipping details order:', newOrder);

        showNotification('ðŸ”„ Vendor-ShipTo and Shipping Details sections reordered!', 'info');
        console.log('ðŸ”„ Vendor-ShipTo + Shipping Details sections reordered via drag & drop!');
        console.log('ðŸ“Š New section order:', newOrder);

        return newOrder;
      });
    } else {
      console.log('ðŸ” No vendor-shipTo + shipping details swap - same position');
    }
  };

  // ============================================================================
  // SECTION SWAPPING FUNCTION
  // ============================================================================



  // Function to swap Section 1 and Section 2
  const handleSwapSections = () => {
    console.log('ðŸ”„ MANUAL SWAP FUNCTION CALLED');
    console.log('ðŸ”„ Before swap - Company fields:', companyFields);
    console.log('ðŸ”„ Before swap - Purchase Order fields:', purchaseOrderFields);

    // Store current values before swapping
    const companyFieldValues = companyFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        value: element ? element.textContent.trim() : ''
      };
    });

    const purchaseOrderFieldValues = purchaseOrderFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        isTitle: field.isTitle, // Explicitly preserve isTitle property
        value: element ? element.textContent.trim() : ''
      };
    });

    console.log('ðŸ” Before swap - Purchase Order fields:', purchaseOrderFieldValues);
    console.log('ðŸ” Title field check:', purchaseOrderFieldValues.find(f => f.isTitle));

    // IMPORTANT: Create new field arrays to avoid reference issues
    const newCompanyFields = purchaseOrderFields.map(field => ({
      ...field,
      isTitle: field.isTitle // Ensure isTitle is preserved
    }));

    const newPurchaseOrderFields = companyFields.map(field => ({
      ...field,
      isTitle: false // Company fields should never be title
    }));

    console.log('ðŸ”„ About to swap fields...');
    console.log('ðŸ”„ New company fields (with title check):', newCompanyFields.map(f => ({ id: f.id, label: f.label, isTitle: f.isTitle })));
    console.log('ðŸ”„ New PO fields (with title check):', newPurchaseOrderFields.map(f => ({ id: f.id, label: f.label, isTitle: f.isTitle })));

    // Swap the fields - keep Purchase Order section intact (title + date + po#)
    setCompanyFields(newCompanyFields);
    setPurchaseOrderFields(newPurchaseOrderFields);

    // Update timestamps
    setLastModified(new Date().toISOString());
    setPoLastModified(new Date().toISOString());

              showNotification('ðŸ”„ Sections swapped!', 'info');

    console.log('ðŸ”„ Sections swapped successfully!');
    console.log('ðŸ“Š New Section 1 (was Section 2):', newCompanyFields);
    console.log('ðŸ“Š New Section 2 (was Section 1):', newPurchaseOrderFields);

    // Log the swap operation
    console.log('ðŸ”„ Section Swap Details:', {
      'Section 1 â†’ Section 2': newCompanyFields.map(f => ({ id: f.id, label: f.label, value: f.value, isTitle: f.isTitle })),
      'Section 2 â†’ Section 1': newPurchaseOrderFields.map(f => ({ id: f.id, label: f.label, value: f.value, isTitle: f.isTitle }))
    });
    };

  // Function to manually swap Vendor and Ship To sections
  const handleSwapVendorShipTo = () => {
    console.log('ðŸ”„ MANUAL VENDOR/SHIP-TO SWAP FUNCTION CALLED');
    console.log('ðŸ”„ Before swap - Vendor fields:', vendorFields);
    console.log('ðŸ”„ Before swap - Ship To fields:', shipToFields);

    // Store current values before swapping
    const vendorFieldValues = vendorFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        value: element ? element.textContent.trim() : ''
      };
    });

    const shipToFieldValues = shipToFields.map(field => {
      const element = document.getElementById(field.id);
      return {
        ...field,
        value: element ? element.textContent.trim() : ''
      };
    });

    console.log('ðŸ” Before swap - Vendor field values:', vendorFieldValues);
    console.log('ðŸ” Before swap - Ship To field values:', shipToFieldValues);

    // Swap the fields
    setVendorFields(shipToFieldValues);
    setShipToFields(vendorFieldValues);

    // Update section order
    setVendorShipToSectionOrder(prevOrder => {
      const newOrder = [...prevOrder].reverse();
      return newOrder;
    });

    showNotification('ðŸ”„ Vendor and Ship To sections swapped!', 'info');

    console.log('ðŸ”„ Vendor and Ship To sections swapped successfully!');
    console.log('ðŸ“Š New Vendor fields (was Ship To):', shipToFieldValues);
    console.log('ðŸ“Š New Ship To fields (was Vendor):', vendorFieldValues);

    // Log the swap operation
    console.log('ðŸ”„ Vendor/Ship-To Swap Details:', {
      'Vendor â†’ Ship To': shipToFieldValues.map(f => ({ id: f.id, label: f.label, value: f.value })),
      'Ship To â†’ Vendor': vendorFieldValues.map(f => ({ id: f.id, label: f.label, value: f.value }))
    });
  };

  // ============================================================================
  // FIELD VALUE CAPTURE
  // ============================================================================

  // Function to capture current field values from the DOM
  const captureFieldValues = () => {
    console.log('ðŸ“ Capturing field values from DOM...');

    // Capture company field values - PRESERVE CURRENT ORDER
    console.log('ðŸ” DEBUG: Current company fields order before capture:', companyFields.map(f => ({ id: f.id, label: f.label })));

    const updatedCompanyFields = companyFields.map(field => {
      const element = document.getElementById(field.id);
      const value = element ? element.textContent.trim() : '';
      console.log(`ðŸ“ Company field "${field.label}": "${value}"`);
      return { ...field, value };
    });

    console.log('ðŸ” DEBUG: Updated company fields order after capture:', updatedCompanyFields.map(f => ({ id: f.id, label: f.label })));

    // Capture purchase order field values
    const updatedPurchaseOrderFields = purchaseOrderFields.map(field => {
      const element = document.getElementById(field.id);
      const value = element ? element.textContent.trim() : '';
      console.log(`ðŸ“ PO field "${field.label}": "${value}"`);
      return { ...field, value };
    });

    // Capture vendor field values
    const updatedVendorFields = vendorFields.map(field => {
      const element = document.getElementById(field.id);
      const value = element ? element.textContent.trim() : '';
      console.log(`ðŸ“ Vendor field "${field.label}": "${value}"`);
      return { ...field, value };
    });

    // Capture ship-to field values
    const updatedShipToFields = shipToFields.map(field => {
      const element = document.getElementById(field.id);
      const value = element ? element.textContent.trim() : '';
      console.log(`ðŸ“ Ship-to field "${field.label}": "${value}"`);
      return { ...field, value };
    });

    // Capture line item values from the table
    const capturedLineItems = [];
    for (let rowIndex = 0; rowIndex < 5; rowIndex++) {
      const rowData = {};
      lineItemColumnOrder.forEach(columnId => {
        const cellSelector = `tr:nth-child(${rowIndex + 1}) td[data-column="${columnId}"] .editable-field`;
        const cell = document.querySelector(`.itemtable tbody ${cellSelector}`);
        const value = cell ? cell.textContent.trim() : '';
        rowData[columnId] = value;
      });
      capturedLineItems.push(rowData);
    }
    console.log('ðŸ“ Line items captured:', capturedLineItems);

    // Capture totals field values using the new state structure
    const updatedTotalsFields = totalsFields.map(field => {
      let value = field.value; // Default to current state value

      // For editable fields, try to get from DOM
      if (!field.isCalculated) {
        const selector = `.totals-section .total-row[data-field="${field.id}"] .editable-field`;
        const element = document.querySelector(selector);
        if (element) {
          value = element.textContent.trim();
        }
      }

      console.log(`ðŸ“ Totals field "${field.label}": "${value}"`);
      return { ...field, value };
    });

    // Update state with captured values
    setCompanyFields(updatedCompanyFields);
    setPurchaseOrderFields(updatedPurchaseOrderFields);
    setVendorFields(updatedVendorFields);
    setShipToFields(updatedShipToFields);
    setTotalsFields(updatedTotalsFields);

    console.log('ðŸ“ Field values captured and state updated!');

    return {
      companyFields: updatedCompanyFields,
      purchaseOrderFields: updatedPurchaseOrderFields,
      vendorFields: updatedVendorFields,
      shipToFields: updatedShipToFields,
      lineItems: capturedLineItems,
      totalsFields: updatedTotalsFields
    };
  };



  return (
    <div className="purchase-order-container">
      {/* Page Header */}
      <header className="page-header">
        <h1>Purchase Order Management System</h1>
        <p>Create, edit, and manage purchase orders with ease</p>
      </header>

      {/* Real-time Status Indicator */}
      <div className="status-indicator">
        <div className="status-grid">
          <div className="status-item">
            <span className="status-label">ðŸ“Š Total Fields:</span>
            <span className="status-value">{getFormStats().totalFields}</span>
          </div>
          <div className="status-item">
            <span className="status-label">ðŸ¢ Company:</span>
            <span className="status-value">{getFormStats().companyFields}</span>
          </div>
          <div className="status-item">
            <span className="status-label">ðŸ“‹ Purchase Order:</span>
            <span className="status-value">{getFormStats().purchaseOrderFields}</span>
          </div>
          <div className="status-item">
            <span className="status-label">ðŸ“ Changes:</span>
            <span className="status-value">{getFormStats().totalChanges}</span>
          </div>
          <div className="status-item">
            <span className="status-label">ðŸ•’ Last Modified:</span>
            <span className="status-value">
              {new Date(getFormStats().lastModified).toLocaleTimeString()}
            </span>
          </div>
          {lastExportTime && (
            <div className="status-item">
              <span className="status-label">ðŸ“„ Last Export:</span>
              <span className="status-value">
                {new Date(lastExportTime).toLocaleTimeString()}
              </span>
            </div>
          )}
        </div>
      </div>

      {/* Purchase Order Form */}
      <div className="container">
        <div className="form-content">
          {/* Header Section */}
          <div className="section-container">
            <div className="header-section">
              {/* Section-level Drag and Drop */}
              <DndContext 
                sensors={sensors}
                collisionDetection={closestCenter}
                onDragEnd={handleSectionDragEnd}
              >
                <SortableContext 
                  items={sections1And2Order}
                  strategy={verticalListSortingStrategy}
                >
                  {/* CRITICAL: Render sections dynamically based on sections1And2Order state */}
                  {sections1And2Order.map(sectionId => {
                    if (sectionId === 'section1') {
                      return (
                        <div key="section1" id="section1" className="sortable-section">
                          <Section1CompanyInfo 
                            companyFields={companyFields}
                            sensors={sensors}
                            onCompanyDragEnd={handleCompanyDragEnd}
                            onAddCompanyField={handleAddCompanyField}
                            onRemoveCompanyField={handleRemoveCompanyField}
                            onLabelChange={handleCompanyFieldLabelChange}
                            onContentChange={handleContentChange}
                            lastModified={lastModified}
                          />
                        </div>
                      );
                    } else if (sectionId === 'section2') {
                      return (
                        <div key="section2" id="section2" className="sortable-section">
                          <Section2PurchaseOrder 
                            purchaseOrderFields={purchaseOrderFields}
                            sensors={sensors}
                            onPurchaseOrderDragEnd={handlePurchaseOrderDragEnd}
                            onAddPurchaseOrderField={handleAddPurchaseOrderField}
                            onRemovePurchaseOrderField={handleRemovePurchaseOrderField}
                            onLabelChange={handlePurchaseOrderFieldLabelChange}
                            onContentChange={handleContentChange}
                            lastModified={poLastModified}
                          />
                        </div>
                      );
                    }
                    return null;
                  })}
                </SortableContext>
              </DndContext>
            </div>
          </div>

          {/* Vendor-ShipTo and Shipping Details Sections (Vertically Swappable) */}
          <DndContext 
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleVendorShipToAndShippingDetailsDragEnd}
          >
            <SortableContext 
              items={vendorShipToAndShippingDetailsOrder}
              strategy={verticalListSortingStrategy}
            >
              {vendorShipToAndShippingDetailsOrder.map(sectionId => {
                if (sectionId === 'sections3And4') {
                  return (
                    <DraggableSectionWrapper key="sections3And4" id="sections3And4" sectionNumber="3&4">
                      {/* Vendor and Ship To Section */}
                      <Section3VendorShipTo 
                        vendorFields={vendorFields}
                        shipToFields={shipToFields}
                        sensors={sensors}
                        onVendorDragEnd={handleVendorDragEnd}
                        onShipToDragEnd={handleShipToDragEnd}
                        onVendorShipToSectionDragEnd={handleVendorShipToSectionDragEnd}
                        vendorShipToSectionOrder={vendorShipToSectionOrder}
                      />
                    </DraggableSectionWrapper>
                  );
                } else if (sectionId === 'sections5And6') {
                  return (
                    <DraggableSectionWrapper key="sections5And6" id="sections5And6" sectionNumber="5&6">
                      {/* Shipping Details Section */}
                      <div className="shipping-details">
                        <div className="section-header">REQUISITIONER</div>
                        <div className="section-header">SHIP VIA</div>
                        <div className="section-header">F.O.B.</div>
                        <div className="section-header">SHIPPING TERMS</div>
                        <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Requisitioner name" /></div>
                        <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping method" /></div>
                        <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="FOB terms" /></div>
                        <div className="shipping-field"><span className="editable-field" contentEditable="true" data-placeholder="Shipping terms" /></div>
                      </div>
                    </DraggableSectionWrapper>
                  );
                }
                return null;
              })}
            </SortableContext>
          </DndContext>

          {/* Items Table */}
          <DndContext 
            sensors={sensors} 
            collisionDetection={closestCenter} 
            onDragEnd={handleLineItemColumnDragEnd}
          >
            <SortableContext items={lineItemColumnOrder} strategy={verticalListSortingStrategy}>
              <table className="itemtable">
                <thead>
                  <tr>
                    <th></th>
                    {lineItemColumnOrder.map((columnId) => {
                      const columnConfig = {
                        itemNumber: { label: 'Item#', colSpan: 3 },
                        description: { label: 'Description', colSpan: 12 },
                        qty: { label: 'Qty', colSpan: 2 },
                      rate: { label: 'Rate', colSpan: 3 },
                        amount: { label: 'Amount', colSpan: 3 }
                      };

                      const config = columnConfig[columnId];
                      return (
                        <th key={columnId} colSpan={config.colSpan} className="draggable-column-header">
                          <SortableColumnHeader id={columnId}>
                            {config.label}
                          </SortableColumnHeader>
                        </th>
                      );
                    })}
                  </tr>
                </thead>
                <tbody>
                  {[1, 2, 3, 4, 5].map((row) => (
                    <tr key={row} className="draggable-row">
                      <td></td>
                      {lineItemColumnOrder.map((columnId) => {
                        const columnConfig = {
                          itemNumber: { colSpan: 3, placeholder: 'Item number' },
                          description: { colSpan: 12, placeholder: 'Item description' },
                          qty: { colSpan: 2, placeholder: 'Qty' },
                          rate: { colSpan: 3, placeholder: '$0.00' },
                          amount: { colSpan: 3, placeholder: '$0.00' }
                        };

                        const config = columnConfig[columnId];
                        return (
                          <td key={`${row}-${columnId}`} colSpan={config.colSpan} data-column={columnId}>
                            <span className="editable-field" contentEditable="true" data-placeholder={config.placeholder} />
                          </td>
                        );
                      })}
                    </tr>
                  ))}
                </tbody>
              </table>
            </SortableContext>
          </DndContext>

          {/* Comments and Totals - Sections 8 & 9 */}
          <DndContext 
            sensors={sensors} 
            collisionDetection={closestCenter} 
            onDragEnd={handleCommentsTotalsDragEnd}
          >
            <SortableContext items={commentsTotalsSectionOrder} strategy={horizontalListSortingStrategy}>
              <div className="comments-totals-section" style={{ display: 'flex', gap: '20px' }}>
                {commentsTotalsSectionOrder.map((sectionId) => {
                  if (sectionId === 'section8') {
                    return (
                      <div key="section8" id="section8" className="sortable-section comments-section" style={{ flex: 1 }}>
                        <DraggableSectionHeader id="section8">
                          Comments or Special Instructions ::
                        </DraggableSectionHeader>
                        <div className="section-content">
                          <div className="comments-content">
                            <span className="editable-field" contentEditable="true" data-placeholder="Enter comments or special instructions..." />
                          </div>
                        </div>
                      </div>
                    );
                  } else if (sectionId === 'section9') {
                    return (
                      <div key="section9" id="section9" className="sortable-section totals-section" style={{ flex: 1 }}>
                        <DraggableSectionHeader id="section9">
                          Totals ::
                        </DraggableSectionHeader>
                        <div className="section-content">
                          <div className="total-row" data-field="subtotal">
                            <span>SUBTOTAL:</span>
                            <span className="calculated-field">$0.00</span>
                          </div>
                          <div className="total-row" data-field="tax">
                            <span>TAX:</span>
                            <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                          </div>
                          <div className="total-row" data-field="shipping">
                            <span>SHIPPING:</span>
                            <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                          </div>
                          <div className="total-row" data-field="other">
                            <span>OTHER:</span>
                            <span className="editable-field" contentEditable="true" data-placeholder="$0.00" />
                          </div>
                          <div className="total-row total-final" data-field="total">
                            <span>TOTAL:</span>
                            <span className="calculated-field total-field">$0.00</span>
                          </div>
                        </div>
                      </div>
                    );
                  }
                  return null;
                })}
              </div>
            </SortableContext>
          </DndContext>

          {/* Contact Information */}
          <div className="contact-section">
            <div className="contact-content">
              <strong>Contact Information:</strong>
              <span className="editable-field" contentEditable="true" data-placeholder="Enter contact information here" />
            </div>
          </div>

          {/* Action Buttons */}
          <div className="button-section">
            <button className="btn preview-btn" onClick={togglePreviewMode}>
              {isPreviewMode ? 'ðŸ‘ï¸ Hide Preview' : 'ðŸ‘ï¸ Show Preview'}
            </button>
            <button className="btn export-btn" onClick={exportToXML}>
              ðŸ“„ Generate XML
            </button>
          </div>

          {/* Change History Display */}
          {changeHistory.length > 0 && (
            <div className="change-history-section">
              <div className="section-header">
                ðŸ“ Change History ({changeHistory.length} changes)
                <button className="clear-history-btn" onClick={clearChangeHistory}>
                  ðŸ—‘ï¸ Clear
                </button>
              </div>
              <div className="change-list">
                {changeHistory.slice(0, 10).map((change) => (
                  <div key={change.id} className="change-item">
                    <span className="change-time">
                      {new Date(change.timestamp).toLocaleTimeString()}
                    </span>
                    <span className="change-type">{change.type}</span>
                    <span className="change-field">{change.fieldId}</span>
                    <span className="change-old">{change.oldValue || 'empty'}</span>
                    <span className="change-arrow">â†’</span>
                    <span className="change-new">{change.newValue || 'empty'}</span>
                  </div>
                ))}
                {changeHistory.length > 10 && (
                  <div className="change-more">
                    ... and {changeHistory.length - 10} more changes
                  </div>
                )}
              </div>
            </div>
          )}

          {/* XML Output Modal */}
          {showXMLModal && (
            <div className="xml-modal-overlay" onClick={closeXMLModal}>
              <div className="xml-modal" onClick={(e) => e.stopPropagation()}>
                <div className="xml-modal-header">
                  <h3>ðŸ“„ Generated XML Output</h3>
                  <button className="xml-modal-close" onClick={closeXMLModal}>Ã—</button>
                </div>
                <div className="xml-modal-content">
                  <div className="xml-output-container">
                    <pre className="xml-code">{xmlOutput}</pre>
                  </div>
                </div>
                <div className="xml-modal-actions">
                  <button className="btn copy-btn" onClick={copyXMLToClipboard}>
                    ðŸ“‹ Copy XML
                  </button>
                  <button className="btn download-btn" onClick={downloadXML}>
                    ðŸ“¥ Download XML
                  </button>
                  <button className="btn close-btn" onClick={closeXMLModal}>
                    Close
                  </button>
                </div>
              </div>
            </div>
          )}






        </div>
      </div>
    </div>
  );
}

export default PurchaseOrderForm;
